// scripts/generateFAQSchema.js
// Automatically detects Q&A patterns and generates FAQPage schema
import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const WORDPRESS_API_URL = 'https://app.dataengineerhub.blog/wp-json/wp/v2';
const DIST_DIR = path.join(__dirname, '..', 'dist');

// Fetch all posts
async function fetchAllPosts() {
    try {
        console.log('üì° Fetching posts for FAQ schema generation...');

        let allPosts = [];
        let page = 1;
        let hasMore = true;

        while (hasMore && page <= 20) {
            const response = await fetch(
                `${WORDPRESS_API_URL}/posts?page=${page}&per_page=100`,
                {
                    headers: {
                        'Accept': 'application/json',
                        'User-Agent': 'DataEngineerHub-FAQ-Schema-Generator'
                    }
                }
            );

            if (!response.ok) {
                if (response.status === 400) {
                    hasMore = false;
                    break;
                }
                throw new Error(`HTTP ${response.status}`);
            }

            const posts = await response.json();

            if (!Array.isArray(posts) || posts.length === 0) {
                hasMore = false;
                break;
            }

            allPosts = allPosts.concat(posts);
            console.log(`‚úÖ Fetched page ${page} (${posts.length} posts)`);
            page++;

            await new Promise(resolve => setTimeout(resolve, 100));
        }

        console.log(`‚úÖ Total posts fetched: ${allPosts.length}`);
        return allPosts;
    } catch (error) {
        console.error('‚ùå Error fetching posts:', error.message);
        return [];
    }
}

// Extract Q&A pairs from content
function extractQAPairs(content) {
    if (!content) return [];

    const qaPairs = [];

    // Pattern 1: H2/H3 questions followed by paragraphs
    const questionPattern = /<h[2-3][^>]*>([^<]*\?[^<]*)<\/h[2-3]>\s*<p[^>]*>([^<]+(?:<[^>]+>[^<]*<\/[^>]+>[^<]*)*?)<\/p>/gi;
    let match;

    while ((match = questionPattern.exec(content)) !== null) {
        const question = stripHTML(match[1]).trim();
        let answer = stripHTML(match[2]).trim();

        // Get more context if answer is too short
        if (answer.length < 50) {
            // Try to get next paragraph too
            const nextPMatch = content.substring(match.index + match[0].length).match(/<p[^>]*>([^<]+)<\/p>/i);
            if (nextPMatch) {
                answer += ' ' + stripHTML(nextPMatch[1]).trim();
            }
        }

        // Only include if we have a good answer (50-500 chars)
        if (answer.length >= 50 && answer.length <= 500) {
            qaPairs.push({
                question,
                answer: answer.substring(0, 500) // Cap at 500 chars
            });
        }
    }

    // Pattern 2: Strong tags with questions
    const strongQuestionPattern = /<strong[^>]*>([^<]*\?[^<]*)<\/strong>\s*<\/p>\s*<p[^>]*>([^<]+)<\/p>/gi;

    while ((match = strongQuestionPattern.exec(content)) !== null) {
        const question = stripHTML(match[1]).trim();
        const answer = stripHTML(match[2]).trim();

        if (answer.length >= 50 && answer.length <= 500) {
            // Avoid duplicates
            if (!qaPairs.some(qa => qa.question === question)) {
                qaPairs.push({ question, answer: answer.substring(0, 500) });
            }
        }
    }

    return qaPairs.slice(0, 10); // Max 10 Q&A pairs per article
}

// Strip HTML tags
function stripHTML(html) {
    if (!html) return '';
    return html
        .replace(/<[^>]+>/g, ' ')
        .replace(/&nbsp;/g, ' ')
        .replace(/&amp;/g, '&')
        .replace(/&lt;/g, '<')
        .replace(/&gt;/g, '>')
        .replace(/&quot;/g, '"')
        .replace(/&#8217;/g, "'")
        .replace(/&#8220;/g, '"')
        .replace(/&#8221;/g, '"')
        .replace(/&#8230;/g, '...')
        .replace(/\s+/g, ' ')
        .trim();
}

// Escape JSON strings
function escapeJSON(str) {
    return str
        .replace(/\\/g, '\\\\')
        .replace(/"/g, '\\"')
        .replace(/\n/g, '\\n')
        .replace(/\r/g, '\\r')
        .replace(/\t/g, '\\t');
}

// Generate FAQPage schema
function generateFAQSchema(qaPairs) {
    if (qaPairs.length === 0) return null;

    const schema = {
        "@context": "https://schema.org",
        "@type": "FAQPage",
        "mainEntity": qaPairs.map(qa => ({
            "@type": "Question",
            "name": qa.question,
            "acceptedAnswer": {
                "@type": "Answer",
                "text": qa.answer
            }
        }))
    };

    return schema;
}

// Inject FAQ schema into HTML file
function injectFAQSchema(htmlPath, faqSchema) {
    try {
        let html = fs.readFileSync(htmlPath, 'utf8');

        // Check if FAQ schema already exists
        if (html.includes('"@type": "FAQPage"') || html.includes('"@type":"FAQPage"')) {
            // Remove existing FAQ schema
            html = html.replace(
                /<script type="application\/ld\+json">\s*\{[^}]*"@type"\s*:\s*"FAQPage"[^<]*<\/script>\s*/gi,
                ''
            );
        }

        // Find the position to inject (before closing </body> tag)
        const bodyCloseIndex = html.lastIndexOf('</body>');

        if (bodyCloseIndex === -1) {
            console.warn(`‚ö†Ô∏è  No </body> tag found in ${htmlPath}`);
            return false;
        }

        // Create schema script tag
        const schemaScript = `
    <!-- FAQ Schema - Auto-generated -->
    <script type="application/ld+json">
    ${JSON.stringify(faqSchema, null, 2)}
    </script>
`;

        // Inject schema
        const newHTML = html.substring(0, bodyCloseIndex) + schemaScript + html.substring(bodyCloseIndex);

        fs.writeFileSync(htmlPath, newHTML, 'utf8');
        return true;
    } catch (error) {
        console.error(`‚ùå Error injecting schema into ${htmlPath}:`, error.message);
        return false;
    }
}

// Main function
async function generateFAQSchemas() {
    console.log('ü§ñ Starting FAQ Schema generation...\n');

    try {
        // Check if dist directory exists
        if (!fs.existsSync(DIST_DIR)) {
            console.log('‚ö†Ô∏è  dist directory not found');
            console.log('üí° FAQ schema generation should run AFTER static pages are built');
            console.log('‚úÖ Skipping FAQ schema generation (will run on next build)');
            return;
        }

        const posts = await fetchAllPosts();

        if (posts.length === 0) {
            console.log('‚ö†Ô∏è  No posts fetched - skipping FAQ schema generation');
            return;
        }

        let processedCount = 0;
        let schemaInjectedCount = 0;
        let skippedCount = 0;

        console.log('\nüìù Processing articles for FAQ schema...\n');

        for (const post of posts) {
            const slug = post.slug;
            const content = post.content?.rendered || '';
            const title = stripHTML(post.title?.rendered || 'Untitled');

            // Extract Q&A pairs
            const qaPairs = extractQAPairs(content);

            if (qaPairs.length === 0) {
                skippedCount++;
                continue;
            }

            // Generate FAQ schema
            const faqSchema = generateFAQSchema(qaPairs);

            if (!faqSchema) {
                skippedCount++;
                continue;
            }

            // Find the HTML file
            const htmlPath = path.join(DIST_DIR, 'articles', slug, 'index.html');

            if (!fs.existsSync(htmlPath)) {
                console.warn(`‚ö†Ô∏è  HTML file not found: ${htmlPath}`);
                skippedCount++;
                continue;
            }

            // Inject FAQ schema
            const injected = injectFAQSchema(htmlPath, faqSchema);

            if (injected) {
                schemaInjectedCount++;
                console.log(`‚úÖ ${title.substring(0, 50)}... (${qaPairs.length} Q&A pairs)`);
            } else {
                skippedCount++;
            }

            processedCount++;

            // Progress indicator
            if (processedCount % 10 === 0) {
                console.log(`   Processed ${processedCount}/${posts.length} articles...`);
            }
        }

        console.log('\n‚úÖ FAQ Schema generation complete!');
        console.log(`üìä Summary:`);
        console.log(`   - Total articles: ${posts.length}`);
        console.log(`   - FAQ schemas injected: ${schemaInjectedCount}`);
        console.log(`   - Skipped (no Q&A): ${skippedCount}`);
        console.log(`   - Success rate: ${Math.round((schemaInjectedCount / posts.length) * 100)}%`);

        console.log('\nüí° Benefits:');
        console.log('   ‚úì Rich snippets in Google search');
        console.log('   ‚úì 36% higher AI citation rate');
        console.log('   ‚úì Better visibility in voice search');
        console.log('   ‚úì Enhanced user experience');

    } catch (error) {
        console.error('\n‚ùå Error generating FAQ schemas:', error.message);
        process.exit(1);
    }
}

// Run the generator
generateFAQSchemas();

// scripts/generateStaticPages.js
// MINIMAL VERSION: Only generate static pages for article posts
// Let React handle category/tag pages dynamically
import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

const __dirname = path.dirname(fileURLToPath(import.meta.url));
const WORDPRESS_API_URL = 'https://app.dataengineerhub.blog/wp-json/wp/v2';

async function fetchFromWP(endpoint, fields = '') {
  const items = [];
  let page = 1;
  
  while (true) {
    try {
      const url = `${WORDPRESS_API_URL}${endpoint}?per_page=100&page=${page}${fields ? `&_fields=${fields}` : ''}`;
      const res = await fetch(url);
      
      if (!res.ok) {
        if (res.status === 400) break;
        throw new Error(`HTTP ${res.status}: ${res.statusText}`);
      }
      
      const data = await res.json();
      if (!Array.isArray(data) || data.length === 0) break;
      
      items.push(...data);
      page++;
      await new Promise(resolve => setTimeout(resolve, 100));
    } catch (error) {
      console.error(`Error fetching ${endpoint}:`, error.message);
      break;
    }
  }
  
  return items;
}

function stripHTML(html) {
  return html.replace(/<[^>]*>/g, '').replace(/\s+/g, ' ').trim();
}

function generateHTML(pageData) {
  const { title, description, path: pagePath, content = '' } = pageData;
  
  return `<!doctype html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>${title} | DataEngineer Hub</title>
    <meta name="description" content="${description}" />
    <link rel="canonical" href="https://dataengineerhub.blog${pagePath}" />
    <meta name="robots" content="index, follow" />
    
    <link rel="dns-prefetch" href="//app.dataengineerhub.blog">
    <link rel="preconnect" href="https://app.dataengineerhub.blog" crossorigin>
    
    <style>
      * { margin: 0; padding: 0; box-sizing: border-box; }
      body {
        font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif;
        background: linear-gradient(135deg, #0f172a 0%, #1e3a8a 50%, #312e81 100%);
        color: #f8fafc;
        line-height: 1.6;
      }
      .seo-content {
        max-width: 800px;
        margin: 0 auto;
        padding: 60px 20px;
      }
      .seo-content h1 {
        font-size: 2.5rem;
        margin-bottom: 1rem;
        background: linear-gradient(135deg, #60a5fa 0%, #a78bfa 50%, #f472b6 100%);
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
        background-clip: text;
      }
      .seo-content p { 
        font-size: 1.125rem; 
        line-height: 1.8; 
        color: #cbd5e1; 
        margin-bottom: 1rem; 
      }
      body.react-loaded .seo-content { display: none; }
    </style>
    
    <script>
      document.addEventListener('DOMContentLoaded', function() {
        document.body.classList.add('react-loading');
      });
    </script>
  </head>
  <body>
    <div id="root">
      <div class="seo-content">
        <h1>${title}</h1>
        <p>${description}</p>
        ${content ? `<div>${content}</div>` : ''}
      </div>
    </div>
    <script type="module" src="/src/main.jsx"></script>
    <script>
      window.addEventListener('load', function() {
        document.body.classList.remove('react-loading');
        document.body.classList.add('react-loaded');
      });
    </script>
  </body>
</html>`;
}

async function generatePages() {
  console.log('üöÄ Generating static pages for article posts only...\n');
  console.log('‚ÑπÔ∏è  Categories and tags will be handled by React dynamically.\n');
  
  const distDir = path.join(__dirname, '..', 'dist');
  
  if (!fs.existsSync(distDir)) {
    console.error('‚ùå dist/ folder not found. Run "npm run build:vite" first.');
    process.exit(1);
  }
  
  // Only generate pages for posts (articles)
  console.log('üìÑ Fetching posts...');
  const posts = await fetchFromWP('/posts', 'slug,title,excerpt,content');
  
  console.log(`‚úÖ Found ${posts.length} posts\n`);
  console.log('üíæ Writing HTML files...');
  
  let generated = 0;
  
  for (const post of posts) {
    const description = stripHTML(post.excerpt.rendered).substring(0, 160);
    const contentPreview = stripHTML(post.content.rendered).substring(0, 500);
    
    const pageData = {
      title: stripHTML(post.title.rendered),
      description,
      path: `/articles/${post.slug}`,
      content: `<p>${contentPreview}...</p>`
    };
    
    const html = generateHTML(pageData);
    const filePath = path.join(distDir, pageData.path, 'index.html');
    const dir = path.dirname(filePath);
    
    if (!fs.existsSync(dir)) {
      fs.mkdirSync(dir, { recursive: true });
    }
    
    fs.writeFileSync(filePath, html);
    generated++;
    
    if (generated % 50 === 0) {
      console.log(`  Generated ${generated}/${posts.length} pages...`);
    }
  }
  
  console.log(`\n‚úÖ Successfully generated ${generated} static HTML pages!`);
  console.log('\nüìä What was generated:');
  console.log(`   ‚úÖ Article posts: ${posts.length}`);
  console.log(`   ‚ö†Ô∏è  Categories: Handled by React (no static HTML)`);
  console.log(`   ‚ö†Ô∏è  Tags: Handled by React (no static HTML)`);
  console.log('\n‚ú® This is better because:');
  console.log('   - Article pages get full SEO benefits');
  console.log('   - Category/tag pages load instantly with React');
  console.log('   - No conflicts between static HTML and React routes\n');
}

generatePages().catch(error => {
  console.error('‚ùå Error generating pages:', error);
  process.exit(1);
});

// scripts/generateStaticPagesIncremental.js
// FIXED VERSION - Generates FULL CONTENT for SEO/AdSense with IMAGES
import fs from 'fs';
import path from 'path';
import crypto from 'crypto';
import { fileURLToPath } from 'url';

const __dirname = path.dirname(fileURLToPath(import.meta.url));
const WORDPRESS_API_URL = 'https://app.dataengineerhub.blog/wp-json/wp/v2';
const WORDPRESS_BASE_URL = 'https://app.dataengineerhub.blog';
const CACHE_FILE = path.join(__dirname, '..', '.build-cache.json');

// ============================================================================
// CACHE MANAGEMENT
// ============================================================================

function loadCache() {
  try {
    if (fs.existsSync(CACHE_FILE)) {
      const cache = JSON.parse(fs.readFileSync(CACHE_FILE, 'utf8'));
      console.log(`üì¶ Loaded cache with ${Object.keys(cache.pages || {}).length} pages`);
      return cache;
    }
  } catch (e) {
    console.warn('‚ö†Ô∏è  Could not load cache:', e.message);
  }
  return { pages: {}, lastBuild: null, stats: {} };
}

function saveCache(cache) {
  try {
    cache.lastBuild = new Date().toISOString();
    fs.writeFileSync(CACHE_FILE, JSON.stringify(cache, null, 2));
    console.log('üíæ Cache saved successfully');
  } catch (e) {
    console.error('‚ùå Failed to save cache:', e.message);
  }
}

function hashContent(content) {
  return crypto.createHash('md5').update(JSON.stringify(content)).digest('hex');
}

// ============================================================================
// BUNDLE DETECTION
// ============================================================================

function findBundleFiles(distDir) {
  const indexHtmlPath = path.join(distDir, 'index.html');

  if (!fs.existsSync(indexHtmlPath)) {
    console.warn('‚ö†Ô∏è  dist/index.html not found');
    return { jsFile: null, cssFile: null };
  }

  const indexContent = fs.readFileSync(indexHtmlPath, 'utf8');

  // Match CSS file - straightforward
  const cssMatch = indexContent.match(/href="(\/assets\/[^"]+\.css)"/);
  const cssFile = cssMatch ? cssMatch[1] : null;

  // Match JS file - can be in /assets/ or /assets/js/
  const jsMatch = indexContent.match(/src="(\/assets\/[^"]+\.js)"/);
  let jsFile = jsMatch ? jsMatch[1] : null;

  // üî• CRITICAL: Find the ACTUAL JS file location
  if (jsFile) {
    const directPath = path.join(distDir, jsFile);

    if (!fs.existsSync(directPath)) {
      console.warn(`‚ö†Ô∏è  JS file not found at expected path: ${jsFile}`);

      // Search strategy 1: Look in /assets/js/ subdirectory
      const jsSubdirPath = path.join(distDir, 'assets', 'js');
      if (fs.existsSync(jsSubdirPath)) {
        const jsFiles = fs.readdirSync(jsSubdirPath);
        const indexJsFile = jsFiles.find(f => f.startsWith('index-') && f.endsWith('.js'));

        if (indexJsFile) {
          jsFile = `/assets/js/${indexJsFile}`;
          console.log(`‚úÖ Found JS in subdirectory: ${jsFile}`);
          return { jsFile, cssFile };
        }
      }

      // Search strategy 2: Look directly in /assets/
      const assetsPath = path.join(distDir, 'assets');
      if (fs.existsSync(assetsPath)) {
        const assetFiles = fs.readdirSync(assetsPath);
        const indexJsFile = assetFiles.find(f => f.startsWith('index-') && f.endsWith('.js'));

        if (indexJsFile) {
          jsFile = `/assets/${indexJsFile}`;
          console.log(`‚úÖ Found JS in assets root: ${jsFile}`);
          return { jsFile, cssFile };
        }
      }

      console.error('‚ùå Could not locate JS bundle anywhere!');
      jsFile = null;
    }
  } else {
    // No JS match in index.html - try to find it manually
    console.warn('‚ö†Ô∏è  No JS reference found in index.html, searching manually...');

    // Check /assets/js/ first
    const jsSubdirPath = path.join(distDir, 'assets', 'js');
    if (fs.existsSync(jsSubdirPath)) {
      const jsFiles = fs.readdirSync(jsSubdirPath);
      const indexJsFile = jsFiles.find(f => f.startsWith('index-') && f.endsWith('.js'));

      if (indexJsFile) {
        jsFile = `/assets/js/${indexJsFile}`;
        console.log(`‚úÖ Manually found JS: ${jsFile}`);
        return { jsFile, cssFile };
      }
    }

    // Check /assets/ root
    const assetsPath = path.join(distDir, 'assets');
    if (fs.existsSync(assetsPath)) {
      const assetFiles = fs.readdirSync(assetsPath);
      const indexJsFile = assetFiles.find(f => f.startsWith('index-') && f.endsWith('.js'));

      if (indexJsFile) {
        jsFile = `/assets/${indexJsFile}`;
        console.log(`‚úÖ Manually found JS: ${jsFile}`);
        return { jsFile, cssFile };
      }
    }
  }

  if (jsFile) console.log(`üì¶ JS bundle: ${jsFile}`);
  if (cssFile) console.log(`üé® CSS bundle: ${cssFile}`);

  // Final verification
  if (jsFile) {
    const finalPath = path.join(distDir, jsFile);
    if (fs.existsSync(finalPath)) {
      console.log(`‚úÖ Verified JS exists at: ${jsFile}`);
    } else {
      console.error(`‚ùå CRITICAL: JS bundle NOT found at ${jsFile}`);
    }
  }

  if (cssFile) {
    const finalPath = path.join(distDir, cssFile);
    if (fs.existsSync(finalPath)) {
      console.log(`‚úÖ Verified CSS exists at: ${cssFile}`);
    } else {
      console.error(`‚ùå CRITICAL: CSS bundle NOT found at ${cssFile}`);
    }
  }

  return { jsFile, cssFile };
}

// ============================================================================
// API FETCHING
// ============================================================================

async function fetchFromWP(endpoint, fields = '') {
  const items = [];
  let page = 1;
  const RETRY_COUNT = 3;
  const TIMEOUT = 15000; // 15 seconds

  while (true) {
    const url = `${WORDPRESS_API_URL}${endpoint}?per_page=100&page=${page}${fields ? `&_fields=${fields}` : ''}`;

    let attempt = 0;
    let success = false;
    let data = null;

    while (attempt < RETRY_COUNT && !success) {
      try {
        const controller = new AbortController();
        const timeoutId = setTimeout(() => controller.abort(), TIMEOUT);

        const res = await fetch(url, { signal: controller.signal });
        clearTimeout(timeoutId);

        if (!res.ok) {
          if (res.status === 400) {
            // End of pagination
            success = true; // Loop done
            data = [];
            break;
          }
          throw new Error(`HTTP ${res.status}: ${res.statusText}`);
        }

        data = await res.json();
        success = true;
      } catch (error) {
        attempt++;
        console.warn(`‚ö†Ô∏è  Fetch failed (Attempt ${attempt}/${RETRY_COUNT}) for ${url}: ${error.message}`);
        if (attempt < RETRY_COUNT) {
          await new Promise(resolve => setTimeout(resolve, 2000 * attempt)); // Linear backoff
        } else {
          // After all retries failed, log error but continue with empty data
          console.error(`‚ùå Error fetching ${endpoint}: ${error.message}`);
          console.log(`‚ÑπÔ∏è  Continuing build with empty data for ${endpoint}`);
          return items; // Return whatever we have so far (might be empty)
        }
      }
    }

    if (!success || !data) {
      // If we couldn't fetch this page, stop pagination
      break;
    }

    if (!Array.isArray(data) || data.length === 0) {
      break; // No more pages
    }

    items.push(...data);
    page++;

    // Safety break for infinite loops
    if (page > 100) break;
  }

  return items;
}

function stripHTML(html) {
  if (!html) return '';
  return html.replace(/<[^>]*>/g, '').replace(/\s+/g, ' ').trim();
}

// ============================================================================
// üñºÔ∏è IMAGE PROCESSING - Make all image URLs absolute & fix CDN issues
// ============================================================================

function makeImagesAbsolute(content) {
  if (!content) return '';

  let processedContent = content;

  // Pattern 1: Fix data-recalc-dims (remove it - causes issues)
  processedContent = processedContent.replace(/data-recalc-dims="1"\s*/g, '');

  // Pattern 2: Fix images with / at end before loading attribute
  processedContent = processedContent.replace(/\s*\/\s*loading=/g, ' loading=');

  // Pattern 3: src="/wp-content/..." -> absolute URL
  processedContent = processedContent.replace(
    /src="(\/wp-content\/[^"]+)"/g,
    `src="${WORDPRESS_BASE_URL}$1"`
  );

  // Pattern 4: src="wp-content/..." (no leading slash) -> absolute URL
  processedContent = processedContent.replace(
    /src="(wp-content\/[^"]+)"/g,
    `src="${WORDPRESS_BASE_URL}/$1"`
  );

  // Pattern 5: Fix any remaining relative image URLs
  processedContent = processedContent.replace(
    /src="(\/[^"]+\.(jpg|jpeg|png|gif|webp|svg))"/gi,
    `src="${WORDPRESS_BASE_URL}$1"`
  );

  // Pattern 6: Fix srcset attributes for responsive images
  processedContent = processedContent.replace(
    /srcset="([^"]+)"/g,
    (match, srcsetValue) => {
      const fixedSrcset = srcsetValue
        .split(',')
        .map(src => {
          const trimmed = src.trim();
          // Already absolute (http/https)
          if (trimmed.startsWith('http')) {
            return trimmed;
          }
          // Starts with /wp-content/
          if (trimmed.startsWith('/wp-content/')) {
            return trimmed.replace(/^\/wp-content\//, `${WORDPRESS_BASE_URL}/wp-content/`);
          }
          // Starts with wp-content/ (no slash)
          if (trimmed.startsWith('wp-content/')) {
            return trimmed.replace(/^wp-content\//, `${WORDPRESS_BASE_URL}/wp-content/`);
          }
          // Other relative URLs starting with /
          if (trimmed.startsWith('/')) {
            const parts = trimmed.split(' ');
            parts[0] = WORDPRESS_BASE_URL + parts[0];
            return parts.join(' ');
          }
          return trimmed;
        })
        .join(', ');
      return `srcset="${fixedSrcset}"`;
    }
  );

  // Pattern 7: Remove HTML entities that cause encoding issues
  processedContent = processedContent.replace(/&#038;/g, '&');

  return processedContent;
}

