const FreshnessBadge = ({ date, modifiedDate }) => {
    if (!date) return null;

    const now = new Date();
    const published = new Date(date);
    const modified = modifiedDate ? new Date(modifiedDate) : null;

    const daysSincePublish = Math.floor((now - published) / (1000 * 60 * 60 * 24));
    const daysSinceModified = modified ? Math.floor((now - modified) / (1000 * 60 * 60 * 24)) : null;

    // Show "NEW" badge for articles published within 7 days
    if (daysSincePublish <= 7) {
        return (
            <span className="inline-flex items-center px-2 py-0.5 bg-green-500/20 border border-green-400/40 text-green-400 text-xs font-semibold rounded-full">
                ‚ú® NEW
            </span>
        );
    }

    // Show "Updated" badge for articles modified within 14 days
    if (daysSinceModified !== null && daysSinceModified <= 14 && daysSinceModified < daysSincePublish) {
        return (
            <span className="inline-flex items-center px-2 py-0.5 bg-blue-500/20 border border-blue-400/40 text-blue-400 text-xs font-semibold rounded-full">
                üîÑ Updated
            </span>
        );
    }

    return null;
};

export default FreshnessBadge;

import React, { useState, useEffect, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Menu, X, Database, ChevronDown, Home, Cloud, Wrench, Code, Tags, Info, Sparkles, Award } from 'lucide-react';

// Helper component for the "Corner Burst" sparks animation
const Spark = ({ x, y, rotate, color }) => {
  const variants = {
    rest: { x: 0, y: 0, scale: 0, opacity: 0 },
    hover: {
      x: x,
      y: y,
      scale: 1,
      opacity: [0, 1, 0.5, 0],
      transition: { duration: 0.7, ease: [0.25, 1, 0.5, 1] },
    },
  };
  return (
    <motion.div
      variants={variants}
      className="absolute top-0 left-0 h-[3px] w-[3px] rounded-full"
      style={{ backgroundColor: color, rotate }}
    />
  );
};

// Category icon provider with original icons
const getCategoryIcon = (category, className = 'h-8 w-8') => {
  const lowerCategory = category.toLowerCase();
  const iconUrls = {
    aws: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/amazonwebservices/amazonwebservices-original-wordmark.svg',
    azure: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/azure/azure-original.svg',
    gcp: 'https://cdn.jsdelivr.net/gh/devicons/devicon@latest/icons/googlecloud/googlecloud-original.svg',
    snowflake: 'https://cdn.brandfetch.io/idJz-fGD_q/theme/dark/symbol.svg?c=1dxbfHSJFAPEGdCLU4o5B',
    airflow: 'https://raw.githubusercontent.com/devicons/devicon/refs/heads/master/icons/apacheairflow/apacheairflow-original.svg',
    dbt: 'https://docs.getdbt.com/img/dbt-logo.svg',
    python: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/python/python-original.svg',
    sql: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/mysql/mysql-original.svg',
    // ‚úÖ NEW: Databricks
    databricks: 'https://cdn.brandfetch.io/idSUrLOWbH/idEHbzBDZC.svg?c=1dxbfHSJFAPEGdCLU4o5B',
    // ‚úÖ NEW: Salesforce  
    salesforce: 'https://raw.githubusercontent.com/devicons/devicon/refs/heads/master/icons/salesforce/salesforce-original.svg'
  };
  
  const iconUrl = iconUrls[lowerCategory];
  
  // Special case for SQL to ensure visibility
  if (lowerCategory === 'sql') {
    return (
      <div className={`${className} bg-slate-200 rounded-full p-1.5 flex items-center justify-center`}>
        <img 
          src={iconUrls.sql} 
          alt={`${category} logo`} 
          className="h-full w-full object-contain" 
        />
      </div>
    );
  }
  
  if (iconUrl) {
    return (
      <img 
        src={iconUrl} 
        alt={`${category} logo`} 
        className={`${className} object-contain`}
        style={{ filter: 'drop-shadow(0px 2px 3px rgba(0, 0, 0, 0.4))' }}
        onError={(e) => {
          e.target.style.display = 'none';
          e.target.parentNode.innerHTML = `<div class="${className} bg-blue-500/20 rounded-lg flex items-center justify-center text-2xl">üìÅ</div>`;
        }}
      />
    );
  }
  
  return (
    <svg viewBox="0 0 24 24" className={className} fill="currentColor">
      <path fill="#6366F1" d="M10 4H4c-1.1 0-2 .9-2 2v12c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V8c0-1.1-.9-2-2-2h-8l-2-2z"/>
    </svg>
  );
};

