// src/components/LazyImage.jsx - OPTIMIZED VERSION
import React, { useEffect, useRef, useState } from 'react';
import { optimizeWordPressImage, generateSrcSet } from '@/utils/imageOptimizer';

const LazyImage = ({ 
  src, 
  alt, 
  width = 800, 
  quality = 80,
  sizes = '100vw',
  className = '',
  priority = false,
  onLoad,
  onError
}) => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [isInView, setIsInView] = useState(priority);
  const [hasError, setHasError] = useState(false);
  const imgRef = useRef(null);
  const observerRef = useRef(null);

  useEffect(() => {
    if (priority) return;

    const setupObserver = () => {
      if (!imgRef.current) return;

      const observer = new IntersectionObserver(
        ([entry]) => {
          if (entry.isIntersecting) {
            setIsInView(true);
            observer.disconnect();
          }
        },
        { 
          rootMargin: '100px', // ‚úÖ Increased from 50px for earlier loading
          threshold: 0.01 
        }
      );

      observer.observe(imgRef.current);
      observerRef.current = observer;
    };

    setupObserver();

    return () => {
      if (observerRef.current) {
        observerRef.current.disconnect();
      }
    };
  }, [priority]);

  const handleLoad = (e) => {
    setIsLoaded(true);
    if (onLoad) onLoad(e);
  };

  const handleError = (e) => {
    setHasError(true);
    if (onError) onError(e);
  };

  const optimizedSrc = optimizeWordPressImage(src, { width, quality });
  const srcSet = generateSrcSet(src, [400, 800, 1200]);

  return (
    <div 
      ref={imgRef} 
      className={`relative ${className}`}
      style={{ 
        minHeight: priority ? 'auto' : '200px' // ‚úÖ Reserve space for non-priority images
      }}
    >
      {!isLoaded && !hasError && !priority && (
        <div className="absolute inset-0 bg-gradient-to-r from-slate-800 via-slate-700 to-slate-800 animate-pulse" />
      )}
      
      {hasError && (
        <div className="absolute inset-0 bg-gradient-to-br from-slate-800 to-slate-700 flex items-center justify-center">
          <div className="text-gray-500 text-center">
            <svg className="h-8 w-8 mx-auto mb-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
            </svg>
            <span className="text-xs">Image not available</span>
          </div>
        </div>
      )}
      
      {(isInView || priority) && !hasError && (
        <img
          src={optimizedSrc}
          srcSet={srcSet}
          sizes={sizes}
          alt={alt}
          loading={priority ? 'eager' : 'lazy'}
          fetchpriority={priority ? 'high' : 'auto'}
          decoding={priority ? 'sync' : 'async'}
          onLoad={handleLoad}
          onError={handleError}
          className={`w-full h-full object-cover transition-opacity duration-300 ${
            isLoaded ? 'opacity-100' : 'opacity-0'
          }`}
          style={{
            contentVisibility: 'auto'
          }}
        />
      )}
    </div>
  );
};

export default LazyImage;

import React, { useEffect } from 'react';

// Mobile-specific optimization component
const MobileOptimization = () => {
  useEffect(() => {
    // Detect mobile device
    const isMobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
    const isSmallScreen = window.innerWidth <= 768;
    
    if (isMobile || isSmallScreen) {
      // Apply mobile-specific optimizations
      optimizeForMobile();
    }

    // Listen for orientation changes and resize events
    const handleResize = () => {
      if (window.innerWidth <= 768) {
        optimizeForMobile();
      } else {
        optimizeForDesktop();
      }
    };

    window.addEventListener('resize', handleResize);
    window.addEventListener('orientationchange', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      window.removeEventListener('orientationchange', handleResize);
    };
  }, []);

  const optimizeForMobile = () => {
    // Reduce image quality and size for mobile
    const images = document.querySelectorAll('img');
    images.forEach(img => {
      if (!img.dataset.optimized) {
        // Add loading="lazy" for better performance
        img.loading = 'lazy';
        img.decoding = 'async';
        
        // Add mobile-specific classes
        img.classList.add('mobile-optimized');
        img.dataset.optimized = 'true';
        
        // Optimize image URLs if they contain size parameters
        if (img.src && img.src.includes('w=')) {
          const mobileUrl = img.src
            .replace(/w=\d+/g, 'w=400')
            .replace(/h=\d+/g, 'h=300')
            .replace(/quality=\d+/g, 'quality=80');
          img.src = mobileUrl;
        }
      }
    });

    // Disable heavy animations on mobile
    document.body.classList.add('mobile-optimized');
    
    // Reduce backdrop-filter blur on mobile for better performance
    const elements = document.querySelectorAll('.glass-effect, .blog-card, .tech-card');
    elements.forEach(el => {
      el.style.backdropFilter = 'blur(5px)';
    });

    // Optimize fonts for mobile
    optimizeFontsForMobile();
  };

  const optimizeForDesktop = () => {
    document.body.classList.remove('mobile-optimized');
    
    // Restore full backdrop-filter blur on desktop
    const elements = document.querySelectorAll('.glass-effect, .blog-card, .tech-card');
    elements.forEach(el => {
      el.style.backdropFilter = '';
    });
  };

  const optimizeFontsForMobile = () => {
    // Ensure fonts are loaded efficiently
    if ('fonts' in document) {
      document.fonts.ready.then(() => {
        // Font loading complete
        console.log('‚úÖ Fonts loaded successfully');
      });
    }

    // Preload critical font weights for mobile
    const criticalFonts = [
      'Inter-400.woff2',
      'Inter-600.woff2',
      'Inter-700.woff2'
    ];

    criticalFonts.forEach(font => {
      const link = document.createElement('link');
      link.rel = 'preload';
      link.as = 'font';
      link.type = 'font/woff2';
      link.crossOrigin = 'anonymous';
      link.href = `https://fonts.gstatic.com/s/inter/v12/${font}`;
      
      if (!document.querySelector(`link[href="${link.href}"]`)) {
        document.head.appendChild(link);
      }
    });
  };

  return null; // This component doesn't render anything
};

export default MobileOptimization;

import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Mail, Send, CheckCircle, Sparkles, Users, Shield } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { useToast } from '@/components/ui/use-toast';
import { useIntersectionObserver } from '@/hooks/useIntersectionObserver';

const Newsletter = () => {
  const [email, setEmail] = useState('');
  const [isSubscribed, setIsSubscribed] = useState(false);
  const [loading, setLoading] = useState(false);
  const { toast } = useToast();
  const [ref, isIntersecting, hasIntersected] = useIntersectionObserver();

  const isValidEmail = (email) => {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    console.log('Form submitted with email:', email);
    
    if (!email.trim()) {
      toast({
        title: "Email Required",
        description: "Please enter your email address to subscribe.",
        variant: "destructive"
      });
      return;
    }

    if (!isValidEmail(email.trim())) {
      toast({
        title: "Invalid Email",
        description: "Please enter a valid email address.",
        variant: "destructive"
      });
      return;
    }

    setLoading(true);

    try {
      // Check if already subscribed
      const subscriptions = JSON.parse(localStorage.getItem('newsletterSubscriptions') || '[]');
      const trimmedEmail = email.trim().toLowerCase();
      
      if (subscriptions.includes(trimmedEmail)) {
        toast({
          title: "Already Subscribed",
          description: "This email address is already on our list!",
        });
        setLoading(false);
        return;
      }

      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1500));

      // Save to localStorage
      subscriptions.push(trimmedEmail);
      localStorage.setItem('newsletterSubscriptions', JSON.stringify(subscriptions));

      // Show success state
      setIsSubscribed(true);
      toast({
        title: "üéâ Successfully Subscribed!",
        description: "Welcome to DataEngineer Hub! You'll receive our latest articles and insights.",
      });
      
      // Reset form after 3 seconds
      setTimeout(() => {
        setIsSubscribed(false);
        setEmail('');
      }, 3000);

    } catch (error) {
      console.error("Subscription error:", error);
      toast({
        title: "Subscription Failed",
        description: "Could not save your subscription. Please try again.",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const handleButtonClick = (e) => {
    e.preventDefault();
    console.log('Button clicked!');
    handleSubmit(e);
  };

  return (
    <section ref={ref} className="py-16 relative overflow-hidden">
      <div className="absolute inset-0">
        <div className="absolute top-10 left-1/4 w-64 h-64 bg-purple-500/10 rounded-full blur-3xl floating-animation"></div>
        <div className="absolute bottom-10 right-1/4 w-80 h-80 bg-blue-500/10 rounded-full blur-3xl floating-animation" style={{ animationDelay: '3s' }}></div>
      </div>

      <div className="container mx-auto px-6 relative z-10">
        <AnimatePresence>
          {hasIntersected && (
            <motion.div
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
              className="max-w-4xl mx-auto"
            >
              <div className="glass-effect rounded-2xl p-8 md:p-12 text-center">
                <motion.div
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  transition={{ duration: 0.6, delay: 0.1 }}
                  className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full mb-6 shadow-xl"
                >
                  <Mail className="h-8 w-8 text-white" />
                </motion.div>

                <motion.h2
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: 0.2 }}
                  className="text-3xl md:text-4xl font-bold mb-4"
                >
                  Stay Ahead of the <span className="gradient-text">Data Curve</span>
                </motion.h2>

                <motion.p
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: 0.3 }}
                  className="text-lg text-gray-300 mb-8 max-w-2xl mx-auto leading-relaxed"
                >
                  Join 25,000+ data professionals who get weekly insights, tutorials, and industry updates 
                  delivered straight to their inbox. No spam, just pure data engineering gold.
                </motion.p>

                <motion.div
                  initial={{ opacity: 0, y: 30 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: 0.4 }}
                  className="max-w-md mx-auto mb-8"
                >
                  <form onSubmit={handleSubmit} className="flex flex-col sm:flex-row gap-4">
                    <div className="flex-1 relative">
                      <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Enter your email address"
                        className="w-full px-6 py-4 bg-white/10 border border-white/20 rounded-full text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent backdrop-blur-sm shadow-lg transition-all duration-300"
                        disabled={isSubscribed || loading}
                      />
                      {isSubscribed && (
                        <motion.div
                          initial={{ scale: 0 }}
                          animate={{ scale: 1 }}
                          className="absolute right-4 top-1/2 transform -translate-y-1/2"
                        >
                          <CheckCircle className="h-6 w-6 text-green-400" />
                        </motion.div>
                      )}
                    </div>
                    <button
                      type="submit"
                      onClick={handleButtonClick}
                      disabled={isSubscribed || loading}
                      className={`px-8 py-4 rounded-full font-bold transition-all duration-300 flex items-center justify-center shadow-xl ${
                        isSubscribed 
                          ? 'bg-green-500 hover:bg-green-600' 
                          : 'bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700'
                      } text-white disabled:opacity-50 disabled:cursor-not-allowed`}
                    >
                      {loading ? (
                        <>
                          <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                          Subscribing...
                        </>
                      ) : isSubscribed ? (
                        <>
                          <CheckCircle className="mr-2 h-5 w-5" />
                          Subscribed!
                        </>
                      ) : (
                        <>
                          Subscribe
                          <Send className="ml-2 h-5 w-5" />
                        </>
                      )}
                    </button>
                  </form>
                </motion.div>

                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ duration: 0.6, delay: 0.5 }}
                  className="flex flex-wrap justify-center items-center gap-8 text-sm text-gray-400"
                >
                  <div className="flex items-center space-x-2">
                    <Users className="h-4 w-4 text-blue-400" />
                    <span>25K+ subscribers</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Sparkles className="h-4 w-4 text-yellow-400" />
                    <span>Weekly insights</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <CheckCircle className="h-4 w-4 text-green-400" />
                    <span>No spam ever</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Shield className="h-4 w-4 text-purple-400" />
                    <span>Unsubscribe anytime</span>
                  </div>
                </motion.div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </section>
  );
};

export default Newsletter;

