// src/components/RecentPosts.jsx - FIXED VERSION WITH ACCESSIBILITY
import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { RefreshCw, AlertCircle, ChevronDown, Grid, List } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { toast } from '@/components/ui/use-toast';
import { usePosts } from '@/hooks/useWordPress';
import { reduceMotion } from '@/utils/performance';

// Import the new components
import PostCard from '@/components/PostCard';
import PostCardSkeleton from '@/components/PostCardSkeleton';
import PostListItem from '@/components/PostListItem';
import PostListItemSkeleton from '@/components/PostListItemSkeleton';

const RecentPosts = ({
  category = null,
  tag = null,
  showCategoryError = false,
  initialLimit = 6,
  title = "Recent Posts",
  showLoadMore = true,
  showViewToggle = true
}) => {
  const [currentPage, setCurrentPage] = useState(1);
  const [sortOrder, setSortOrder] = useState('desc');
  const [viewMode, setViewMode] = useState('grid');
  const shouldReduceMotion = reduceMotion();

  const postsPerPage = initialLimit;

  const {
    posts,
    loading,
    error,
    totalPages,
    totalPosts,
    hasMore,
    refresh
  } = usePosts({
    page: currentPage,
    per_page: postsPerPage,
    categorySlug: category,
    tag: tag,
    orderby: 'date',
    order: sortOrder,
  });

  useEffect(() => {
    setCurrentPage(1);
  }, [sortOrder, category, tag]);

  const handleRefresh = () => {
    console.log('üîÑ Manual refresh triggered in RecentPosts');
    refresh();
    toast({
      title: "Refreshing posts...",
      description: "Loading latest content",
    });
  };

  const toggleSortOrder = () => {
    const newOrder = sortOrder === 'desc' ? 'asc' : 'desc';
    console.log('üîÑ Changing sort order to:', newOrder);
    setSortOrder(newOrder);
    setCurrentPage(1);
  };

  const loadMorePosts = () => {
    if (!loading && hasMore) {
      console.log('üìÑ Loading more posts, next page:', currentPage + 1);
      setCurrentPage(prev => prev + 1);
    }
  };

  const renderSkeletons = () => {
    return Array.from({ length: initialLimit }).map((_, i) => (
      viewMode === 'list'
        ? <PostListItemSkeleton key={`skel-list-${i}`} />
        : <PostCardSkeleton key={`skel-grid-${i}`} />
    ));
  };

  const containerClasses = viewMode === 'list'
    ? 'flex flex-col space-y-4 max-w-4xl mx-auto'
    : 'grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6';

  if (loading && posts.length === 0) {
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between flex-wrap gap-4">
          <h2 className="text-2xl font-bold gradient-text">{title}</h2>
          {showViewToggle && (
            <div className="flex items-center space-x-2">
              <Button 
                variant="outline" 
                size="sm" 
                onClick={() => setViewMode('grid')} 
                className={viewMode === 'grid' ? "bg-blue-600 text-white min-h-[44px] min-w-[44px]" : "border-blue-400/50 text-blue-300 hover:bg-blue-500/20 min-h-[44px] min-w-[44px]"}
                aria-label="Switch to grid view"
                title="Grid view"
              >
                <Grid className="h-4 w-4" />
                <span className="sr-only">Grid view</span>
              </Button>
              <Button 
                variant="outline" 
                size="sm" 
                onClick={() => setViewMode('list')} 
                className={viewMode === 'list' ? "bg-blue-600 text-white min-h-[44px] min-w-[44px]" : "border-blue-400/50 text-blue-300 hover:bg-blue-500/20 min-h-[44px] min-w-[44px]"}
                aria-label="Switch to list view"
                title="List view"
              >
                <List className="h-4 w-4" />
                <span className="sr-only">List view</span>
              </Button>
            </div>
          )}
        </div>
        <div className={containerClasses}>{renderSkeletons()}</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="space-y-6">
        <h2 className="text-2xl font-bold gradient-text">{title}</h2>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-red-500/10 border border-red-500/20 rounded-lg p-6 text-center"
        >
          <AlertCircle className="h-12 w-12 text-red-400 mx-auto mb-4" />
          <h3 className="text-lg font-semibold text-red-300 mb-2">
            {(category || tag) && showCategoryError 
              ? `${category ? `Category "${category}"` : `Tag "${tag}"`} Issue` 
              : 'Failed to Load Posts'}
          </h3>
          <p className="text-red-200/80 mb-4">{error}</p>
          <Button
            onClick={handleRefresh}
            variant="outline"
            className="border-red-400/50 text-red-300 hover:bg-red-500/20"
            aria-label="Try again"
          >
            <RefreshCw className="h-4 w-4 mr-2" />
            Try Again
          </Button>
        </motion.div>
      </div>
    );
  }

  if (posts.length === 0 && !loading) {
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <h2 className="text-2xl font-bold gradient-text">{title}</h2>
        </div>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-yellow-500/10 border border-yellow-500/20 rounded-lg p-8 text-center"
        >
          <h3 className="text-lg font-semibold text-yellow-300 mb-2">
            {category 
              ? `No posts found in "${category}" category` 
              : tag
              ? `No posts found with tag "${tag}"`
              : 'No posts available'}
          </h3>
          <p className="text-yellow-200/80 mb-4">
            {category
              ? 'This category exists but has no published posts yet.'
              : tag
              ? 'This tag exists but has no published posts yet.'
              : 'No posts have been published yet.'
            }
          </p>
        </motion.div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between flex-wrap gap-4">
        <div className="flex items-center gap-4">
          <h2 className="text-2xl font-bold gradient-text">{title}</h2>
          {totalPosts > 0 && (
            <span className="text-sm text-gray-400 bg-gray-800/50 px-3 py-1 rounded-full">
              {posts.length} of {totalPosts} posts
            </span>
          )}
        </div>
        <div className="flex items-center space-x-2">
          {showViewToggle && (
            <div className="flex items-center space-x-2 mr-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setViewMode('grid')}
                className={viewMode === 'grid'
                  ? "bg-blue-600 text-white min-h-[44px] min-w-[44px]"
                  : "border-blue-400/50 text-blue-300 hover:bg-blue-500/20 min-h-[44px] min-w-[44px]"
                }
                aria-label="Switch to grid view"
                title="Grid view"
              >
                <Grid className="h-4 w-4" />
                <span className="sr-only">Grid view</span>
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setViewMode('list')}
                className={viewMode === 'list'
                  ? "bg-blue-600 text-white min-h-[44px] min-w-[44px]"
                  : "border-blue-400/50 text-blue-300 hover:bg-blue-500/20 min-h-[44px] min-w-[44px]"
                }
                aria-label="Switch to list view"
                title="List view"
              >
                <List className="h-4 w-4" />
                <span className="sr-only">List view</span>
              </Button>
            </div>
          )}

          <Button
            variant="outline"
            size="sm"
            onClick={toggleSortOrder}
            className="border-purple-400/50 text-purple-300 hover:bg-purple-500/20"
            title={sortOrder === 'desc' ? 'Click for oldest first' : 'Click for newest first'}
            aria-label={sortOrder === 'desc' ? 'Sort oldest first' : 'Sort newest first'}
          >
            <svg
              className="h-4 w-4 mr-2"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              {sortOrder === 'desc' ? (
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 4h13M3 8h9m-9 4h6m4 0l4-4m0 0l4 4m-4-4v12" />
              ) : (
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 4h13M3 8h9m-9 4h9m5-4v12m0 0l-4-4m4 4l4-4" />
              )}
            </svg>
            {sortOrder === 'desc' ? 'Newest First' : 'Oldest First'}
          </Button>

          <Button
            onClick={handleRefresh}
            variant="outline"
            size="sm"
            className="border-blue-400/50 text-blue-300 hover:bg-blue-500/20"
            disabled={loading}
            aria-label="Refresh posts"
          >
            <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
            Refresh
          </Button>
        </div>
      </div>

      <motion.div
        key={viewMode}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.3 }}
        className={containerClasses}
      >
        {posts.map((post, index) => (
          <motion.div
            key={post.id}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: index * 0.05 }}
          >
            {viewMode === 'list' ? <PostListItem post={post} /> : <PostCard post={post} />}
          </motion.div>
        ))}
      </motion.div>

      {showLoadMore && hasMore && (
        <div className="flex justify-center pt-8">
          <Button
            onClick={loadMorePosts}
            disabled={loading}
            className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white px-8 py-3 rounded-full font-medium transition-all duration-300 transform hover:scale-105"
            aria-label="Load more posts"
          >
            {loading ? (
              <>
                <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
                Loading more...
              </>
            ) : (
              <>
                <ChevronDown className="h-4 w-4 mr-2" />
                Load More Posts
              </>
            )}
          </Button>
        </div>
      )}

      {loading && posts.length > 0 && (
        <div className={containerClasses}>
          {Array.from({ length: Math.min(postsPerPage, 3) }).map((_, i) => (
            viewMode === 'list'
              ? <PostListItemSkeleton key={`loading-list-${i}`} />
              : <PostCardSkeleton key={`loading-grid-${i}`} />
          ))}
        </div>
      )}

      {!hasMore && posts.length > initialLimit && (
        <div className="text-center py-8">
          <div className="inline-flex items-center space-x-2 text-gray-400 bg-gray-800/30 px-4 py-2 rounded-full">
            <span className="w-2 h-2 bg-gray-400 rounded-full"></span>
            <span className="text-sm">You've reached the end of the posts</span>
            <span className="w-2 h-2 bg-gray-400 rounded-full"></span>
          </div>
        </div>
      )}
    </div>
  );
};

export default RecentPosts

