export default RelatedArticles;

// src/components/SEO/Breadcrumbs.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import { ChevronRight, Home } from 'lucide-react';
import { getBreadcrumbSchema } from '@/lib/seoConfig';

/**
 * Breadcrumbs Component
 * Provides visual navigation and structured data for SEO
 */
const Breadcrumbs = ({ breadcrumbs, className = '' }) => {
    if (!breadcrumbs || breadcrumbs.length <= 1) return null;

    // Generate structured data
    const structuredData = getBreadcrumbSchema(breadcrumbs);

    return (
        <>
            {/* Structured Data */}
            <script type="application/ld+json">
                {JSON.stringify(structuredData)}
            </script>

            {/* Visual Breadcrumbs */}
            <nav
                aria-label="Breadcrumb"
                className={`flex items-center space-x-2 text-sm ${className}`}
            >
                <ol className="flex items-center space-x-2 flex-wrap">
                    {breadcrumbs.map((crumb, index) => {
                        const isLast = index === breadcrumbs.length - 1;
                        const isFirst = index === 0;

                        return (
                            <li key={crumb.url} className="flex items-center">
                                {index > 0 && (
                                    <ChevronRight className="h-4 w-4 text-gray-500 mx-2" aria-hidden="true" />
                                )}

                                {isLast ? (
                                    <span
                                        className="text-gray-400 font-medium truncate max-w-xs"
                                        aria-current="page"
                                    >
                                        {crumb.name}
                                    </span>
                                ) : (
                                    <Link
                                        to={crumb.url.replace('https://dataengineerhub.blog', '')}
                                        className="text-blue-400 hover:text-blue-300 transition-colors flex items-center gap-1"
                                    >
                                        {isFirst && <Home className="h-4 w-4" aria-hidden="true" />}
                                        <span className="hover:underline">{crumb.name}</span>
                                    </Link>
                                )}
                            </li>
                        );
                    })}
                </ol>
            </nav>
        </>
    );
};

export default Breadcrumbs;

// src/components/SEO/MetaTags.jsx - ENHANCED WITH CTR OPTIMIZATION
import React from 'react';
import { Helmet } from 'react-helmet-async';
import {
  SITE_CONFIG,
  formatTitle,
  formatDescription,
  generateKeywords,
  getCanonicalUrl,
  ensureAbsoluteUrl,
  getImageUrl,
  formatDate,
  getArticleSchema,
  getWebSiteSchema,
  getBreadcrumbSchema,
} from '@/lib/seoConfig';
import { optimizeMetaDescription } from '@/lib/metaDescriptionOptimizer';

const MetaTags = ({
  title,
  description,
  keywords,
  image,
  url,
  type = 'website',
  author = SITE_CONFIG.author.name,
  publishedTime,
  modifiedTime,
  category,
  tags = [],
  noindex = false,
  breadcrumbs = null,
  faqSchema = null,
  howToSchema = null,
  readTime, // NEW: for meta description optimization
  optimizeDescription = true, // NEW: enable/disable optimization
}) => {
  // Format title
  const fullTitle = formatTitle(title);

  // Optimize description for better CTR
  let fullDescription;
  if (optimizeDescription && type === 'article' && title) {
    // Use smart optimizer for articles
    fullDescription = optimizeMetaDescription({
      title,
      excerpt: description,
      category,
      tags,
      readTime: readTime || 5,
    });
  } else {
    // Use standard formatting for other pages
    fullDescription = formatDescription(description);
  }

  // Generate canonical URL
  const currentUrl = url ? ensureAbsoluteUrl(url) : (typeof window !== 'undefined' ? window.location.href : SITE_CONFIG.url);

  // Get image URL
  const fullImage = getImageUrl(image);

  // Format dates
  const formattedPublishedTime = formatDate(publishedTime);
  const formattedModifiedTime = formatDate(modifiedTime || publishedTime);

  // Generate keywords
  const finalKeywords = keywords || generateKeywords(tags, category);

  // Generate structured data
  const articleSchema = type === 'article' && formattedPublishedTime
    ? getArticleSchema({
      title: title || fullTitle,
      description: fullDescription,
      url: currentUrl,
      image: fullImage,
      datePublished: formattedPublishedTime,
      dateModified: formattedModifiedTime,
      category,
      tags,
      author,
    })
    : null;

  const websiteSchema = type === 'website' ? getWebSiteSchema() : null;

  const breadcrumbSchema = breadcrumbs && breadcrumbs.length > 0
    ? getBreadcrumbSchema(breadcrumbs)
    : null;

  return (
    <Helmet>
      <html lang="en" />
      <title>{fullTitle}</title>
      <meta name="description" content={fullDescription} />
      {finalKeywords && <meta name="keywords" content={finalKeywords} />}
      <meta name="author" content={author} />
      <link rel="canonical" href={currentUrl} />

      {/* Robots directives */}
      {noindex ? (
        <meta name="robots" content="noindex, nofollow" />
      ) : (
        <>
          <meta name="robots" content="index, follow, max-image-preview:large, max-snippet:-1, max-video-preview:-1" />
          <meta name="googlebot" content="index, follow, max-snippet:-1, max-image-preview:large, max-video-preview:-1" />
          <meta name="bingbot" content="index, follow, max-snippet:-1, max-image-preview:large, max-video-preview:-1" />
        </>
      )}

      {/* Open Graph */}
      <meta property="og:type" content={type} />
      <meta property="og:url" content={currentUrl} />
      <meta property="og:title" content={fullTitle} />
      <meta property="og:description" content={fullDescription} />
      <meta property="og:image" content={fullImage} />
      <meta property="og:image:width" content="1200" />
      <meta property="og:image:height" content="630" />
      <meta property="og:image:alt" content={title || SITE_CONFIG.name} />
      <meta property="og:site_name" content={SITE_CONFIG.name} />
      <meta property="og:locale" content="en_US" />

      {/* Twitter Card */}
      <meta name="twitter:card" content="summary_large_image" />
      <meta name="twitter:url" content={currentUrl} />
      <meta name="twitter:title" content={fullTitle} />
      <meta name="twitter:description" content={fullDescription} />
      <meta name="twitter:image" content={fullImage} />
      <meta name="twitter:image:alt" content={title || SITE_CONFIG.name} />
      <meta name="twitter:site" content={SITE_CONFIG.social.twitter} />
      <meta name="twitter:creator" content={SITE_CONFIG.social.twitter} />

      {/* Article-specific meta tags */}
      {type === 'article' && formattedPublishedTime && (
        <>
          <meta property="article:author" content={author} />
          <meta property="article:published_time" content={formattedPublishedTime} />
          {formattedModifiedTime && (
            <>
              <meta property="article:modified_time" content={formattedModifiedTime} />
              <meta property="og:updated_time" content={formattedModifiedTime} />
            </>
          )}
          {category && <meta property="article:section" content={category} />}
          {Array.isArray(tags) && tags.map((tag, i) => {
            const tagName = typeof tag === 'string' ? tag : tag.name;
            return tagName ? <meta property="article:tag" content={tagName} key={`article-tag-${i}`} /> : null;
          })}
        </>
      )}

      {/* Mobile optimization */}
      <meta name="viewport" content="width=device-width, initial-scale=1.0, maximum-scale=5.0" />
      <meta name="theme-color" content="#1e293b" />
      <meta name="mobile-web-app-capable" content="yes" />
      <meta name="apple-mobile-web-app-capable" content="yes" />
      <meta name="apple-mobile-web-app-status-bar-style" content="black-translucent" />

      {/* Structured Data - Article */}
      {articleSchema && (
        <script type="application/ld+json">
          {JSON.stringify(articleSchema)}
        </script>
      )}

      {/* Structured Data - Breadcrumbs */}
      {breadcrumbSchema && (
        <script type="application/ld+json">
          {JSON.stringify(breadcrumbSchema)}
        </script>
      )}

      {/* Structured Data - Website */}
      {websiteSchema && (
        <script type="application/ld+json">
          {JSON.stringify(websiteSchema)}
        </script>
      )}

      {/* Structured Data - FAQ (if provided) */}
      {faqSchema && (
        <script type="application/ld+json">
          {JSON.stringify(faqSchema)}
        </script>
      )}

      {/* Structured Data - HowTo (if provided) */}
      {howToSchema && (
        <script type="application/ld+json">
          {JSON.stringify(howToSchema)}
        </script>
      )}
    </Helmet>
  );
};

export default MetaTags;

import { generateSitemap } from 'react-router-sitemap';
import { allArticles } from '@/data/articles';

// Generate dynamic routes for articles
const generateArticleRoutes = () => {
  return allArticles.map(article => ({
    path: `/articles/${article.slug}`,
    lastmod: article.date,
    priority: 0.8,
    changefreq: 'weekly'
  }));
};

// Generate category routes
const generateCategoryRoutes = () => {
  const categories = ['aws', 'snowflake', 'azure', 'sql', 'airflow', 'dbt', 'python', 'gcp'];
  return categories.map(category => ({
    path: `/category/${category}`,
    priority: 0.7,
    changefreq: 'daily'
  }));
};

// Static routes
const staticRoutes = [
  { path: '/', priority: 1.0, changefreq: 'daily' },
  { path: '/articles', priority: 0.9, changefreq: 'daily' },
  { path: '/about', priority: 0.6, changefreq: 'monthly' },
  { path: '/contact', priority: 0.5, changefreq: 'monthly' },
  { path: '/newsletter', priority: 0.7, changefreq: 'weekly' },
  { path: '/privacy-policy', priority: 0.3, changefreq: 'yearly' },
  { path: '/terms-of-service', priority: 0.3, changefreq: 'yearly' }
];

export const generateSitemapXML = () => {
  const allRoutes = [
    ...staticRoutes,
    ...generateArticleRoutes(),
    ...generateCategoryRoutes()
  ];

  const sitemap = `<?xml version="1.0" encoding="UTF-8"?>
<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">
${allRoutes.map(route => `
  <url>
    <loc>https://dataengineerhub.blog${route.path}</loc>
    ${route.lastmod ? `<lastmod>${new Date(route.lastmod).toISOString().split('T')[0]}</lastmod>` : ''}
    <priority>${route.priority}</priority>
    <changefreq>${route.changefreq}</changefreq>
  </url>
`).join('')}
</urlset>`;

  return sitemap;
};

// Generate robots.txt
export const generateRobotsTxt = () => {
  return `User-agent: *
Allow: /

Sitemap: https://dataengineerhub.blog/sitemap.xml

# Block access to admin areas
Disallow: /admin/
Disallow: /wp-admin/
Disallow: /wp-includes/
Disallow: /wp-content/plugins/
Disallow: /wp-content/themes/

# Allow access to CSS and JS files
Allow: /wp-content/themes/*.css
Allow: /wp-content/themes/*.js
Allow: /wp-includes/*.css
Allow: /wp-includes/*.js`;
};

// src/components/TagsList.jsx - FIXED VERSION WITH ACCESSIBILITY
import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { Tag } from 'lucide-react';

const TagsList = ({ tags, limit = null, showIcon = true, size = 'default' }) => {
  if (!tags || tags.length === 0) return null;

  const displayTags = limit ? tags.slice(0, limit) : tags;

  // Size variants - UPDATED WITH PROPER TOUCH TARGETS
  const sizeClasses = {
    small: 'px-3 py-1.5 text-xs min-h-[44px] min-w-[44px]',
    default: 'px-4 py-2 text-sm min-h-[48px]',
    large: 'px-5 py-2.5 text-base min-h-[48px]'
  };

  const iconSizes = {
    small: 'h-3 w-3',
    default: 'h-4 w-4',
    large: 'h-5 w-5'
  };

  return (
    <div className="flex flex-wrap items-center gap-2">
      {showIcon && (
        <Tag className={`${iconSizes[size]} text-gray-400 flex-shrink-0`} />
      )}
      {displayTags.map((tag, index) => (
        <motion.div
          key={tag.id || tag.slug}
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
