// AI Insights Component
// Analyze patterns across all scanned articles

import React, { useMemo } from 'react';
import { Sparkles, TrendingUp, AlertTriangle, CheckCircle, Lightbulb, ArrowRight } from 'lucide-react';

export function AIInsights({ results }) {
    const insights = useMemo(() => {
        if (!results || results.length === 0) return null;

        // Aggregate all issues
        const issueCount = {};
        const strengthCount = {};
        let totalScore = 0;
        let totalWords = 0;

        results.forEach(r => {
            totalScore += r.score || 0;
            totalWords += r.wordCount || 0;

            // Count issues from topIssues
            (r.topIssues || []).forEach(issue => {
                const key = issue.label || issue.type;
                if (key) {
                    issueCount[key] = (issueCount[key] || 0) + 1;
                }
            });

            // Count missing elements
            if (!r.hasTLDR) issueCount['Missing TL;DR'] = (issueCount['Missing TL;DR'] || 0) + 1;
            if (!r.hasLastUpdated) issueCount['No Last Updated'] = (issueCount['No Last Updated'] || 0) + 1;
            if (r.questions < 3) issueCount['Few Questions'] = (issueCount['Few Questions'] || 0) + 1;
            if (r.authorityLinks < 2) issueCount['Few Authority Links'] = (issueCount['Few Authority Links'] || 0) + 1;

            // Count strengths
            if (r.hasTLDR) strengthCount['Has TL;DR'] = (strengthCount['Has TL;DR'] || 0) + 1;
            if (r.questions >= 3) strengthCount['Good Q&A'] = (strengthCount['Good Q&A'] || 0) + 1;
            if ((r.codeBlocks || 0) >= 3) strengthCount['Rich Code'] = (strengthCount['Rich Code'] || 0) + 1;
        });

        // Calculate averages
        const avgScore = Math.round(totalScore / results.length);

        // Sort issues by frequency
        const topIssues = Object.entries(issueCount)
            .map(([label, count]) => ({
                label,
                count,
                percent: Math.round((count / results.length) * 100)
            }))
            .sort((a, b) => b.count - a.count)
            .slice(0, 5);

        // Generate quick wins
        const quickWins = [];
        if (issueCount['Missing TL;DR'] > 0) {
            quickWins.push({
                action: 'Add TL;DR summary',
                affected: issueCount['Missing TL;DR'],
                impact: '+20% AI visibility',
                effort: '2 min/article'
            });
        }
        if (issueCount['Few Questions'] > 0) {
            quickWins.push({
                action: 'Add FAQ section',
                affected: issueCount['Few Questions'],
                impact: '+36% citation rate',
                effort: '5 min/article'
            });
        }
        if (issueCount['No Last Updated'] > 0) {
            quickWins.push({
                action: 'Add Last Updated date',
                affected: issueCount['No Last Updated'],
                impact: '+2x freshness signal',
                effort: '1 min/article'
            });
        }
        if (issueCount['Few Authority Links'] > 0) {
            quickWins.push({
                action: 'Add authority citations',
                affected: issueCount['Few Authority Links'],
                impact: '+15% E-E-A-T score',
                effort: '5 min/article'
            });
        }

        return {
            totalArticles: results.length,
            avgScore,
            topIssues,
            quickWins: quickWins.slice(0, 3),
            goodArticles: results.filter(r => r.score >= 70).length,
            criticalArticles: results.filter(r => r.score < 40).length
        };
    }, [results]);

    if (!insights) {
        return (
            <div className="bg-slate-800/50 rounded-2xl border border-slate-700 p-6 text-center">
                <Sparkles className="w-12 h-12 text-gray-600 mx-auto mb-3" />
                <p className="text-gray-400">Run a scan to see AI-powered insights</p>
            </div>
        );
    }

    return (
        <div className="bg-gradient-to-br from-purple-900/30 to-pink-900/30 rounded-2xl border border-purple-500/30 overflow-hidden">
            {/* Header */}
            <div className="px-6 py-4 bg-slate-900/30 border-b border-purple-500/20">
                <div className="flex items-center gap-3">
                    <div className="p-2 bg-purple-500/20 rounded-lg">
                        <Sparkles className="w-6 h-6 text-purple-400" />
                    </div>
                    <div>
                        <h3 className="text-xl font-bold text-white">AI Insights</h3>
                        <p className="text-sm text-gray-400">{insights.totalArticles} articles analyzed</p>
                    </div>
                    <div className="ml-auto text-right">
                        <div className="text-3xl font-black text-white">{insights.avgScore}</div>
                        <div className="text-sm text-gray-400">Avg Score</div>
                    </div>
                </div>
            </div>

            <div className="p-6 space-y-6">
                {/* Common Issues */}
                {insights.topIssues.length > 0 && (
                    <div>
                        <h4 className="text-sm font-bold text-gray-300 mb-3 flex items-center gap-2">
                            <AlertTriangle className="w-4 h-4 text-yellow-400" />
                            Common Issues
                        </h4>
                        <div className="space-y-2">
                            {insights.topIssues.map((issue, idx) => (
                                <div key={idx} className="flex items-center gap-3 bg-slate-900/30 rounded-lg p-3">
                                    <div className="flex-1">
                                        <span className="text-white text-sm">{issue.label}</span>
                                    </div>
                                    <div className="flex items-center gap-2">
                                        <span className="text-yellow-400 text-sm font-bold">{issue.percent}%</span>
                                        <span className="text-gray-500 text-xs">({issue.count} articles)</span>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                )}

                {/* Quick Wins */}
                {insights.quickWins.length > 0 && (
                    <div>
                        <h4 className="text-sm font-bold text-gray-300 mb-3 flex items-center gap-2">
                            <Lightbulb className="w-4 h-4 text-green-400" />
                            Quick Wins
                        </h4>
                        <div className="grid gap-3">
                            {insights.quickWins.map((win, idx) => (
                                <div key={idx} className="flex items-center gap-4 bg-green-500/10 border border-green-500/30 rounded-lg p-4">
                                    <div className="flex-1">
                                        <div className="text-white font-medium">{win.action}</div>
                                        <div className="text-xs text-gray-400 mt-1">
                                            {win.affected} articles ‚Ä¢ {win.effort}
                                        </div>
                                    </div>
                                    <div className="text-right">
                                        <div className="text-green-400 font-bold text-sm">{win.impact}</div>
                                    </div>
                                    <ArrowRight className="w-5 h-5 text-green-400" />
                                </div>
                            ))}
                        </div>
                    </div>
                )}

                {/* Summary Stats */}
                <div className="grid grid-cols-3 gap-4 pt-4 border-t border-purple-500/20">
                    <div className="text-center">
                        <div className="text-2xl font-bold text-green-400">{insights.goodArticles}</div>
                        <div className="text-xs text-gray-400">Good (70+)</div>
                    </div>
                    <div className="text-center">
                        <div className="text-2xl font-bold text-yellow-400">
                            {insights.totalArticles - insights.goodArticles - insights.criticalArticles}
                        </div>
                        <div className="text-xs text-gray-400">Needs Work</div>
                    </div>
                    <div className="text-center">
                        <div className="text-2xl font-bold text-red-400">{insights.criticalArticles}</div>
                        <div className="text-xs text-gray-400">Critical</div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default AIInsights;

// Advanced Filters Component
// Filter articles by search, category, score range, and issue type

import React from 'react';
import { Search, Filter, X, ChevronDown } from 'lucide-react';

export function AdvancedFilters({ filters, onFiltersChange, categories = [] }) {
    const scoreRanges = [
        { id: 'all', label: 'All Scores', min: 0, max: 100 },
        { id: 'critical', label: 'Critical (<40)', min: 0, max: 39 },
        { id: 'needs-work', label: 'Needs Work (40-69)', min: 40, max: 69 },
        { id: 'good', label: 'Good (70+)', min: 70, max: 100 }
    ];

    const handleSearchChange = (value) => {
        onFiltersChange({ ...filters, search: value });
    };

    const handleCategoryChange = (value) => {
        onFiltersChange({ ...filters, category: value || null });
    };

    const handleScoreChange = (rangeId) => {
        const range = scoreRanges.find(r => r.id === rangeId);
        onFiltersChange({
            ...filters,
            scoreMin: range?.min ?? 0,
            scoreMax: range?.max ?? 100
        });
    };

    const handleReset = () => {
        onFiltersChange({
            search: '',
            category: null,
            scoreMin: 0,
            scoreMax: 100
        });
    };

    const hasActiveFilters = filters.search || filters.category ||
        filters.scoreMin > 0 || filters.scoreMax < 100;

    const currentScoreRange = scoreRanges.find(r =>
        r.min === filters.scoreMin && r.max === filters.scoreMax
    )?.id || 'all';

    return (
        <div className="bg-slate-800/50 rounded-xl border border-slate-700 p-4">
            <div className="flex flex-wrap gap-3 items-center">
                {/* Search */}
                <div className="relative flex-1 min-w-[200px]">
                    <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-500" />
                    <input
                        type="text"
                        value={filters.search || ''}
                        onChange={(e) => handleSearchChange(e.target.value)}
                        placeholder="Search by title or URL..."
                        className="w-full pl-10 pr-4 py-2 bg-slate-900/50 border border-slate-600 rounded-lg text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500 text-sm"
                    />
                </div>

                {/* Category Dropdown */}
                {categories.length > 0 && (
                    <div className="relative">
                        <select
                            value={filters.category || ''}
                            onChange={(e) => handleCategoryChange(e.target.value)}
                            className="appearance-none px-4 py-2 pr-8 bg-slate-900/50 border border-slate-600 rounded-lg text-white text-sm focus:outline-none focus:ring-2 focus:ring-purple-500 cursor-pointer"
                        >
                            <option value="">All Categories</option>
                            {categories.map(cat => (
                                <option key={cat} value={cat}>{cat}</option>
                            ))}
                        </select>
                        <ChevronDown className="absolute right-2 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-500 pointer-events-none" />
                    </div>
                )}

                {/* Score Range Dropdown */}
                <div className="relative">
                    <select
                        value={currentScoreRange}
                        onChange={(e) => handleScoreChange(e.target.value)}
                        className="appearance-none px-4 py-2 pr-8 bg-slate-900/50 border border-slate-600 rounded-lg text-white text-sm focus:outline-none focus:ring-2 focus:ring-purple-500 cursor-pointer"
                    >
                        {scoreRanges.map(range => (
                            <option key={range.id} value={range.id}>{range.label}</option>
                        ))}
                    </select>
                    <ChevronDown className="absolute right-2 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-500 pointer-events-none" />
                </div>

                {/* Reset Button */}
                {hasActiveFilters && (
                    <button
                        onClick={handleReset}
                        className="flex items-center gap-1.5 px-3 py-2 text-gray-400 hover:text-white hover:bg-slate-700 rounded-lg transition-colors text-sm"
                    >
                        <X className="w-4 h-4" />
                        Reset
                    </button>
                )}
            </div>

            {/* Active Filters Summary */}
            {hasActiveFilters && (
                <div className="flex items-center gap-2 mt-3 pt-3 border-t border-slate-700">
                    <Filter className="w-4 h-4 text-purple-400" />
                    <span className="text-sm text-gray-400">Active filters:</span>
                    {filters.search && (
                        <span className="px-2 py-0.5 bg-purple-500/20 text-purple-300 rounded text-xs">
                            "{filters.search}"
                        </span>
                    )}
                    {filters.category && (
                        <span className="px-2 py-0.5 bg-blue-500/20 text-blue-300 rounded text-xs">
                            {filters.category}
                        </span>
                    )}
                    {(filters.scoreMin > 0 || filters.scoreMax < 100) && (
                        <span className="px-2 py-0.5 bg-green-500/20 text-green-300 rounded text-xs">
                            Score: {filters.scoreMin}-{filters.scoreMax}
                        </span>
                    )}
                </div>
            )}
        </div>
    );
}

export default AdvancedFilters;

// Article Selector Component
// Checkbox list for selecting articles to scan

import React from 'react';
import { CheckSquare, Square, MinusSquare } from 'lucide-react';

export function ArticleSelector({ articles, selected, onSelectionChange, disabled }) {
    const allSelected = articles.length > 0 && selected.size === articles.length;
    const someSelected = selected.size > 0 && selected.size < articles.length;

    const handleSelectAll = () => {
        if (allSelected) {
            onSelectionChange(new Set());
        } else {
            onSelectionChange(new Set(articles.map(a => a.id || a.url)));
        }
    };

    const handleToggle = (id) => {
        const newSelected = new Set(selected);
        if (newSelected.has(id)) {
            newSelected.delete(id);
        } else {
            newSelected.add(id);
        }
        onSelectionChange(newSelected);
    };

    return (
        <div className="bg-slate-800/50 rounded-2xl border border-slate-700 overflow-hidden">
            {/* Header */}
            <div className="flex items-center justify-between px-4 py-3 bg-slate-900/50 border-b border-slate-700">
                <button
                    onClick={handleSelectAll}
                    disabled={disabled || articles.length === 0}
                    className="flex items-center gap-2 text-gray-300 hover:text-white disabled:opacity-50 transition-colors"
                >
                    {allSelected ? (
                        <CheckSquare className="w-5 h-5 text-purple-400" />
                    ) : someSelected ? (
                        <MinusSquare className="w-5 h-5 text-purple-400" />
                    ) : (
                        <Square className="w-5 h-5" />
                    )}
                    <span className="text-sm font-medium">
                        {allSelected ? 'Deselect All' : 'Select All'}
                    </span>
                </button>
                <span className="text-sm text-gray-400">
                    {selected.size} of {articles.length} selected
                </span>
            </div>

            {/* Article List */}
            <div className="max-h-64 overflow-y-auto divide-y divide-slate-700/50">
                {articles.length === 0 ? (
                    <div className="px-4 py-8 text-center text-gray-500">
                        No articles to display
                    </div>
                ) : (
                    articles.map((article) => {
                        const id = article.id || article.url;
                        const isSelected = selected.has(id);
                        return (
                            <div
                                key={id}
                                onClick={() => !disabled && handleToggle(id)}
                                className={`flex items-center gap-3 px-4 py-3 cursor-pointer transition-colors ${isSelected ? 'bg-purple-500/10' : 'hover:bg-slate-700/30'
                                    } ${disabled ? 'opacity-50 cursor-not-allowed' : ''}`}
                            >
                                {isSelected ? (
                                    <CheckSquare className="w-5 h-5 text-purple-400 flex-shrink-0" />
                                ) : (
                                    <Square className="w-5 h-5 text-gray-500 flex-shrink-0" />
                                )}
                                <div className="flex-1 min-w-0">
                                    <div className="text-white text-sm font-medium truncate">
                                        {article.title || 'Untitled'}
                                    </div>
                                    {article.category && (
                                        <div className="text-xs text-gray-500 mt-0.5">
                                            {article.category}
                                        </div>
                                    )}
                                </div>
                                {article.score !== undefined && (
                                    <span className={`text-sm font-bold ${article.score >= 70 ? 'text-green-400' :
                                            article.score >= 40 ? 'text-yellow-400' : 'text-red-400'
                                        }`}>
                                        {article.score}
                                    </span>
                                )}
                            </div>
                        );
                    })
                )}
            </div>
        </div>
    );
}

export default ArticleSelector;

// Competitor Comparison Component
// Compare your site against competitor sites

import React, { useState } from 'react';
import { Trophy, TrendingUp, TrendingDown, Minus, Loader2, AlertCircle, Target } from 'lucide-react';

export function CompetitorComparison({ yourResults, competitorResults, onLoadCompetitor }) {
    const [competitorUrl, setCompetitorUrl] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleLoadCompetitor = async () => {
        if (!competitorUrl.trim()) {
            setError('Please enter a competitor feed URL');
            return;
        }
        setLoading(true);
        setError('');
        try {
            await onLoadCompetitor(competitorUrl);
        } catch (err) {
            setError(err.message || 'Failed to load competitor data');
        } finally {
            setLoading(false);
        }
    };

    // Calculate metrics
    const calcAvg = (results, field) => {
        if (!results || results.length === 0) return 0;
        return Math.round(results.reduce((sum, r) => sum + (r[field] || 0), 0) / results.length);
    };

    const yourMetrics = yourResults && yourResults.length > 0 ? {
        count: yourResults.length,
        avgScore: calcAvg(yourResults, 'score'),
        avgWords: calcAvg(yourResults, 'wordCount'),
        avgQuestions: calcAvg(yourResults, 'questions'),
        avgAuthority: calcAvg(yourResults, 'authorityLinks')
    } : null;

    const competitorMetrics = competitorResults && competitorResults.length > 0 ? {
        count: competitorResults.length,
        avgScore: calcAvg(competitorResults, 'score'),
        avgWords: calcAvg(competitorResults, 'wordCount'),
        avgQuestions: calcAvg(competitorResults, 'questions'),
        avgAuthority: calcAvg(competitorResults, 'authorityLinks')
    } : null;

    const getDiffIcon = (yours, theirs) => {
        if (yours > theirs) return { icon: TrendingUp, color: 'text-green-400', bg: 'bg-green-500/20' };
        if (yours < theirs) return { icon: TrendingDown, color: 'text-red-400', bg: 'bg-red-500/20' };
        return { icon: Minus, color: 'text-gray-400', bg: 'bg-gray-500/20' };
    };

    const metrics = [
        { label: 'Average Score', field: 'avgScore', suffix: '' },
        { label: 'Word Count', field: 'avgWords', suffix: '' },
        { label: 'Questions', field: 'avgQuestions', suffix: '' },
        { label: 'Authority Links', field: 'avgAuthority', suffix: '' }
    ];

    // Determine winner
    let yourWins = 0;
    let theirWins = 0;
    if (yourMetrics && competitorMetrics) {
        metrics.forEach(m => {
            if (yourMetrics[m.field] > competitorMetrics[m.field]) yourWins++;
            else if (yourMetrics[m.field] < competitorMetrics[m.field]) theirWins++;
        });
    }

    return (
        <div className="bg-slate-800/50 rounded-2xl border border-slate-700 overflow-hidden">
            {/* Header */}
            <div className="px-6 py-4 bg-slate-900/50 border-b border-slate-700">
                <div className="flex items-center gap-3">
                    <div className="p-2 bg-yellow-500/20 rounded-lg">
                        <Trophy className="w-6 h-6 text-yellow-400" />
                    </div>
                    <div>
                        <h3 className="text-xl font-bold text-white">Competitor Comparison</h3>
                        <p className="text-sm text-gray-400">Compare your content against competitors</p>
                    </div>
                </div>
            </div>

            <div className="p-6">
                {/* Competitor URL Input */}
                {!competitorMetrics && (
                    <div className="mb-6">
                        <label className="block text-sm text-gray-400 mb-2">
                            Load competitor sitemap or feed URL:
                        </label>
                        <div className="flex gap-2">
                            <input
                                type="url"
                                value={competitorUrl}
                                onChange={(e) => { setCompetitorUrl(e.target.value); setError(''); }}
                                placeholder="https://competitor.com/sitemap.xml"
                                className="flex-1 px-4 py-2 bg-slate-900/50 border border-slate-600 rounded-lg text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500"
                            />
                            <button
                                onClick={handleLoadCompetitor}
                                disabled={loading}
                                className="px-6 py-2 bg-purple-600 hover:bg-purple-700 text-white rounded-lg flex items-center gap-2 disabled:opacity-50 transition-colors"
                            >
                                {loading ? <Loader2 className="w-4 h-4 animate-spin" /> : <Target className="w-4 h-4" />}
                                Load
                            </button>
                        </div>
                        {error && (
                            <div className="mt-2 flex items-center gap-2 text-red-400 text-sm">
                                <AlertCircle className="w-4 h-4" />
                                {error}
                            </div>
                        )}
                    </div>
                )}

                {/* Comparison Table */}
                {yourMetrics && competitorMetrics ? (
                    <>
                        {/* Winner Banner */}
                        {yourWins !== theirWins && (
                            <div className={`mb-4 p-3 rounded-lg flex items-center gap-2 ${yourWins > theirWins
                                    ? 'bg-green-500/20 border border-green-500/30 text-green-400'
                                    : 'bg-red-500/20 border border-red-500/30 text-red-400'
                                }`}>
                                <Trophy className="w-5 h-5" />
                                <span className="font-medium">
                                    {yourWins > theirWins
                                        ? `You're winning! ${yourWins} of ${metrics.length} metrics are better.`
                                        : `Competitor leads in ${theirWins} of ${metrics.length} metrics.`
                                    }
                                </span>
                            </div>
                        )}

                        <table className="w-full">
                            <thead>
                                <tr className="border-b border-slate-700">
                                    <th className="text-left py-3 text-sm text-gray-400 font-medium">Metric</th>
                                    <th className="text-center py-3 text-sm text-gray-400 font-medium">You ({yourMetrics.count})</th>
                                    <th className="text-center py-3 text-sm text-gray-400 font-medium">Competitor ({competitorMetrics.count})</th>
                                    <th className="text-center py-3 text-sm text-gray-400 font-medium">Diff</th>
                                </tr>
                            </thead>
                            <tbody>
                                {metrics.map(m => {
                                    const yours = yourMetrics[m.field];
                                    const theirs = competitorMetrics[m.field];
                                    const diff = yours - theirs;
                                    const { icon: Icon, color, bg } = getDiffIcon(yours, theirs);

                                    return (
                                        <tr key={m.field} className="border-b border-slate-700/50">
                                            <td className="py-3 text-white">{m.label}</td>
                                            <td className="py-3 text-center text-white font-bold">{yours}{m.suffix}</td>
                                            <td className="py-3 text-center text-gray-400">{theirs}{m.suffix}</td>
                                            <td className="py-3 text-center">
                                                <span className={`inline-flex items-center gap-1 px-2 py-1 rounded ${bg} ${color}`}>
                                                    <Icon className="w-3 h-3" />
                                                    {diff > 0 ? '+' : ''}{diff}
                                                </span>
                                            </td>
                                        </tr>
                                    );
                                })}
                            </tbody>
                        </table>
                    </>
                ) : !competitorMetrics && yourMetrics ? (
                    <div className="text-center py-8">
                        <Target className="w-12 h-12 text-gray-600 mx-auto mb-3" />
                        <p className="text-gray-400">Load a competitor feed to compare</p>
                    </div>
                ) : (
                    <div className="text-center py-8">
                        <Trophy className="w-12 h-12 text-gray-600 mx-auto mb-3" />
                        <p className="text-gray-400">Scan your articles first, then load competitor data</p>
                    </div>
                )}
            </div>
        </div>
    );
}

export default CompetitorComparison;

// Feed URL Input Component
// Parse sitemap, RSS, JSON, or plain text feeds

import React, { useState } from 'react';
import { Rss, Loader2, Check, AlertCircle, FileText, Code, List } from 'lucide-react';
import feedParser from '@/services/feedParser';

export function FeedUrlInput({ onUrlsLoad }) {
    const [feedUrl, setFeedUrl] = useState('');
    const [format, setFormat] = useState('auto');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [result, setResult] = useState(null);

    const formats = [
        { id: 'auto', label: 'Auto-detect', icon: FileText },
        { id: 'sitemap', label: 'Sitemap', icon: Code },
        { id: 'rss', label: 'RSS/Atom', icon: Rss },
        { id: 'json', label: 'JSON', icon: Code },
        { id: 'text', label: 'Plain Text', icon: List }
    ];

    const handleLoad = async () => {
        if (!feedUrl.trim()) {
            setError('Please enter a feed URL');
            return;
        }

        setLoading(true);
        setError('');
        setResult(null);

        try {
            const urls = await feedParser.parseUrl(feedUrl, format);
            if (urls.length === 0) {
                setError('No URLs found in the feed');
            } else {
                setResult({ count: urls.length, urls });
            }
        } catch (err) {
            setError(err.message || 'Failed to parse feed');
        } finally {
            setLoading(false);
        }
    };

    const handleConfirm = () => {
        if (result?.urls) {
            onUrlsLoad(result.urls.map(url => ({
                url,
                title: new URL(url).pathname.split('/').filter(Boolean).pop() || 'Untitled'
            })));
        }
    };

    return (
        <div className="bg-slate-800/50 rounded-2xl border border-slate-700 p-6">
            <h3 className="text-lg font-bold text-white mb-4 flex items-center gap-2">
                <Rss className="w-5 h-5 text-purple-400" />
                Feed / Sitemap URL
            </h3>

            <div className="flex gap-2 mb-4">
                <input
                    type="url"
                    value={feedUrl}
                    onChange={(e) => { setFeedUrl(e.target.value); setError(''); setResult(null); }}
                    placeholder="https://example.com/sitemap.xml"
                    className="flex-1 bg-slate-900/50 border border-slate-600 rounded-lg px-4 py-3 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500"
                />
                <button
                    onClick={handleLoad}
                    disabled={loading || !feedUrl.trim()}
                    className="px-6 py-3 bg-purple-600 hover:bg-purple-700 text-white rounded-lg flex items-center gap-2 disabled:opacity-50 transition-colors"
                >
                    {loading ? <Loader2 className="w-5 h-5 animate-spin" /> : <Check className="w-5 h-5" />}
                    {loading ? 'Loading...' : 'Load'}
                </button>
            </div>

            <div className="flex flex-wrap gap-2 mb-4">
                {formats.map(f => (
                    <button
                        key={f.id}
                        onClick={() => setFormat(f.id)}
                        className={`px-3 py-1.5 rounded-lg text-sm flex items-center gap-1.5 transition-colors ${format === f.id
                                ? 'bg-purple-600 text-white'
                                : 'bg-slate-700/50 text-gray-400 hover:text-white'
                            }`}
                    >
                        <f.icon className="w-3.5 h-3.5" />
                        {f.label}
                    </button>
                ))}
            </div>

            {error && (
                <div className="p-3 bg-red-500/10 border border-red-500/30 rounded-lg flex items-center gap-2 text-red-400">
                    <AlertCircle className="w-5 h-5 flex-shrink-0" />
                    <span className="text-sm">{error}</span>
                </div>
            )}

            {result && (
                <div className="p-4 bg-green-500/10 border border-green-500/30 rounded-lg">
                    <div className="flex items-center justify-between">
                        <div className="flex items-center gap-2 text-green-400">
                            <Check className="w-5 h-5" />
                            <span className="font-medium">Found {result.count} URLs</span>
                        </div>
                        <button
                            onClick={handleConfirm}
                            className="px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg text-sm font-medium transition-colors"
                        >
                            Load All URLs
                        </button>
                    </div>
                    <div className="mt-3 max-h-32 overflow-y-auto">
                        <div className="text-xs text-gray-400 space-y-1">
                            {result.urls.slice(0, 5).map((url, i) => (
                                <div key={i} className="truncate">{url}</div>
                            ))}
                            {result.urls.length > 5 && (
                                <div className="text-gray-500">...and {result.urls.length - 5} more</div>
                            )}
                        </div>
                    </div>
                </div>
            )}

            <div className="mt-4 text-xs text-gray-500">
                Supports: XML Sitemap, RSS/Atom feeds, JSON feeds, plain text (one URL per line)
            </div>
        </div>
    );
}

export default FeedUrlInput;

// Manual URL Input Component
// Textarea for pasting URLs to scan

import React, { useState, useEffect } from 'react';
import { Link, Trash2, Check, AlertCircle } from 'lucide-react';

export function ManualUrlInput({ onUrlsLoad, initialUrls = [] }) {
    const [input, setInput] = useState('');
    const [urls, setUrls] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        if (initialUrls.length > 0) {
            setInput(initialUrls.join('\n'));
            setUrls(initialUrls);
        }
    }, []);

    const parseUrls = (text) => {
        const parsed = text
            .split(/[\n\r]+/)
            .map(line => line.trim())
            .filter(line => {
                try {
                    if (!line.startsWith('http')) return false;
                    new URL(line);
                    return true;
                } catch {
                    return false;
                }
            });
        return [...new Set(parsed)]; // Remove duplicates
    };

    const handleInputChange = (e) => {
        const text = e.target.value;
        setInput(text);
        setError('');
        const parsed = parseUrls(text);
        setUrls(parsed);
    };

    const handleLoad = () => {
        if (urls.length === 0) {
            setError('No valid URLs found. Enter URLs starting with http:// or https://');
            return;
        }
        onUrlsLoad(urls.map(url => ({ url, title: new URL(url).pathname.split('/').pop() || 'Untitled' })));
    };

    const handleClear = () => {
        setInput('');
        setUrls([]);
        setError('');
    };

    return (
        <div className="bg-slate-800/50 rounded-2xl border border-slate-700 p-6">
            <h3 className="text-lg font-bold text-white mb-4 flex items-center gap-2">
                <Link className="w-5 h-5 text-purple-400" />
                Manual URL Input
            </h3>

            <textarea
                value={input}
                onChange={handleInputChange}
                placeholder={`Paste URLs here, one per line:\nhttps://example.com/article-1\nhttps://medium.com/@user/post\nhttps://dev.to/user/article`}
                className="w-full h-40 bg-slate-900/50 border border-slate-600 rounded-xl p-4 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500 font-mono text-sm resize-none"
            />

            {error && (
                <div className="mt-2 flex items-center gap-2 text-red-400 text-sm">
                    <AlertCircle className="w-4 h-4" />
                    {error}
                </div>
            )}

            <div className="flex items-center justify-between mt-4">
                <div className="flex items-center gap-2">
                    <span className={`text-sm ${urls.length > 0 ? 'text-green-400' : 'text-gray-400'}`}>
                        {urls.length} valid URL{urls.length !== 1 ? 's' : ''} detected
                    </span>
                    {urls.length > 0 && urls.length < input.split('\n').filter(l => l.trim()).length && (
                        <span className="text-yellow-400 text-xs">
                            ({input.split('\n').filter(l => l.trim()).length - urls.length} invalid)
                        </span>
                    )}
                </div>
                <div className="flex gap-2">
                    <button
                        onClick={handleClear}
                        className="px-4 py-2 text-gray-400 hover:text-white flex items-center gap-2 transition-colors"
                    >
                        <Trash2 className="w-4 h-4" />
                        Clear
                    </button>
                    <button
                        onClick={handleLoad}
                        disabled={urls.length === 0}
                        className="px-6 py-2 bg-purple-600 hover:bg-purple-700 text-white rounded-lg flex items-center gap-2 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                    >
                        <Check className="w-4 h-4" />
                        Load {urls.length} URLs
                    </button>
                </div>
            </div>
        </div>
    );
}

export default ManualUrlInput;

// Scan Queue Component
// Queue management with pause, resume, and progress tracking

import React from 'react';
import { Play, Pause, Square, Trash2, CheckCircle, Loader2, Clock, AlertCircle } from 'lucide-react';
import { motion } from 'framer-motion';

export function ScanQueue({
    queue,
    currentIndex,
    status, // 'idle' | 'running' | 'paused' | 'completed'
    onStart,
    onPause,
    onResume,
    onCancel,
    onClear
}) {
    const completedCount = queue.filter(item => item.status === 'done').length;
    const errorCount = queue.filter(item => item.status === 'error').length;
    const progress = queue.length > 0 ? (completedCount / queue.length) * 100 : 0;

    const getStatusIcon = (itemStatus) => {
        switch (itemStatus) {
            case 'done': return <CheckCircle className="w-4 h-4 text-green-400" />;
            case 'scanning': return <Loader2 className="w-4 h-4 text-purple-400 animate-spin" />;
            case 'error': return <AlertCircle className="w-4 h-4 text-red-400" />;
            default: return <Clock className="w-4 h-4 text-gray-500" />;
        }
    };

    const getStatusColor = (itemStatus) => {
        switch (itemStatus) {
            case 'done': return 'bg-green-500/10 border-green-500/30';
            case 'scanning': return 'bg-purple-500/10 border-purple-500/30';
            case 'error': return 'bg-red-500/10 border-red-500/30';
            default: return 'bg-slate-800/50 border-slate-700';
        }
    };

    if (queue.length === 0) return null;

    return (
        <div className="bg-slate-800/50 rounded-2xl border border-slate-700 overflow-hidden">
            {/* Header */}
            <div className="flex items-center justify-between px-6 py-4 bg-slate-900/50 border-b border-slate-700">
                <div className="flex items-center gap-3">
                    <div className="p-2 bg-purple-500/20 rounded-lg">
                        <Play className="w-5 h-5 text-purple-400" />
                    </div>
                    <div>
                        <h3 className="text-white font-bold">Scan Queue</h3>
                        <p className="text-sm text-gray-400">{queue.length} articles</p>
                    </div>
                </div>
                <div className="flex items-center gap-2">
                    {status === 'idle' && (
                        <button
                            onClick={onStart}
                            className="px-4 py-2 bg-purple-600 hover:bg-purple-700 text-white rounded-lg flex items-center gap-2 transition-colors"
                        >
                            <Play className="w-4 h-4" />
                            Start
                        </button>
                    )}
                    {status === 'running' && (
                        <button
                            onClick={onPause}
                            className="px-4 py-2 bg-yellow-600 hover:bg-yellow-700 text-white rounded-lg flex items-center gap-2 transition-colors"
                        >
                            <Pause className="w-4 h-4" />
                            Pause
                        </button>
