// ‚úÖ KEY FIX: Don't render immediately - wait for DOM
const initApp = () => {
  const root = ReactDOM.createRoot(document.getElementById('root'));

  root.render(
    <React.StrictMode>
      <HelmetProvider>
        <BrowserRouter>
          <React.Suspense fallback={<PageLoader />}>
