                                                    {term.keywords?.slice(0, 3).length > 0 && (
                                                        <div className="flex flex-wrap gap-1 mt-3">
                                                            {term.keywords.slice(0, 3).map((keyword, idx) => (
                                                                <span
                                                                    key={idx}
                                                                    className="text-xs px-2 py-0.5 bg-slate-700/50 text-gray-400 rounded"
