/**
 * Advanced Bulk Scan Page
 * Features:
 * 1. Manual URL Input
 * 2. Article Selection
 * 3. Custom Feed Parser (Sitemap, RSS, JSON, Text)
 * 4. AI-Powered Insights
 * 5. Competitor Comparison
 * 6. Advanced Filters
 * 7. Scan Queue Management
 */

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
    Layers, Play, Loader2, CheckCircle, AlertCircle, ArrowUpDown,
    ChevronDown, ChevronRight, AlertTriangle, TrendingUp, TrendingDown,
    Download, FileText
} from 'lucide-react';
import wordpressApi from '@/services/wordpressApi';
import { analyzeArticleFull } from '@/utils/seo';
import scanHistory from '@/services/scanHistory';
import feedParser from '@/services/feedParser';
import contentOptimizerService from '@/services/contentOptimizerService';
import pdfExportService from '@/services/pdfExportService';
import { SEOScoreCard } from '@/components/admin/SEOScoreCard';

// Import new bulk scan components
import {
    ScanSourceSelector,
    ManualUrlInput,
    FeedUrlInput,
    ArticleSelector,
    AdvancedFilters,
    ScanQueue,
    AIInsights,
    CompetitorComparison
} from '@/components/admin/bulk-scan';

export function BulkScanPage() {
    // Data sources
    const [scanSource, setScanSource] = useState('wordpress'); // 'wordpress' | 'manual' | 'feed'
    const [articles, setArticles] = useState([]);
    const [manualUrls, setManualUrls] = useState([]);
    const [feedUrls, setFeedUrls] = useState([]);

    // Selection
    const [selectedArticles, setSelectedArticles] = useState(new Set());

    // Scanning state
    const [loading, setLoading] = useState(false);
    const [queue, setQueue] = useState([]);
    const [queueStatus, setQueueStatus] = useState('idle'); // 'idle' | 'running' | 'paused' | 'completed'
    const [currentIndex, setCurrentIndex] = useState(0);

    // Results
    const [results, setResults] = useState([]);
    const [competitorResults, setCompetitorResults] = useState([]);

    // Filters & sorting
    const [filters, setFilters] = useState({
        search: '',
        category: null,
        scoreMin: 0,
        scoreMax: 100
    });
    const [sortBy, setSortBy] = useState('score');
    const [sortDir, setSortDir] = useState('asc');
    const [expandedRows, setExpandedRows] = useState(new Set());

    // Refs for pause/cancel
    const scanningRef = useRef(false);
    const pausedRef = useRef(false);

    // Load WordPress articles on mount
    useEffect(() => {
        loadArticles();
    }, []);

    const loadArticles = async () => {
        setLoading(true);
        try {
            const data = await wordpressApi.getAllPosts(1, 100);
            setArticles(data.posts || []);
            // Select all by default
            setSelectedArticles(new Set((data.posts || []).map(a => a.id)));
        } catch (err) {
            console.error('Failed to load articles:', err);
            setArticles([]);
        }
        setLoading(false);
    };

    // Handle manual URL input
    const handleManualUrls = (urls) => {
        setManualUrls(urls);
        setSelectedArticles(new Set(urls.map(u => u.url)));
    };

    // Handle feed URL parsing
    const handleFeedUrls = (urls) => {
        setFeedUrls(urls);
        setSelectedArticles(new Set(urls.map(u => u.url)));
    };

    // Get current data source
    const getCurrentArticles = () => {
        switch (scanSource) {
            case 'manual': return manualUrls;
            case 'feed': return feedUrls;
            default: return articles;
        }
    };

    // Filter articles based on filters
    const getFilteredArticles = useCallback(() => {
        let items = getCurrentArticles();

        if (filters.search) {
            const search = filters.search.toLowerCase();
            items = items.filter(a =>
                (a.title || '').toLowerCase().includes(search) ||
                (a.url || a.slug || '').toLowerCase().includes(search)
            );
        }

        if (filters.category) {
            items = items.filter(a => a.category === filters.category);
        }

        return items;
    }, [scanSource, articles, manualUrls, feedUrls, filters]);

    // Get unique categories
    const getCategories = () => {
        const cats = new Set();
        getCurrentArticles().forEach(a => {
            if (a.category) cats.add(a.category);
        });
        return [...cats];
    };

    // Build scan queue from selection
    const buildQueue = () => {
        const items = getFilteredArticles().filter(a =>
            selectedArticles.has(a.id || a.url)
        );
        return items.map((a, i) => ({
            id: a.id || a.url || i,
            title: a.title || 'Untitled',
            url: a.url || `${window.location.origin}/articles/${a.slug}`,
            slug: a.slug,
            content: a.content || '',
            category: a.category,
            status: 'queued'
        }));
    };

    // Start scanning
    const handleStartScan = async () => {
        const newQueue = buildQueue();
        if (newQueue.length === 0) return;

        setQueue(newQueue);
        setQueueStatus('running');
        setCurrentIndex(0);
        setResults([]);
        scanningRef.current = true;
        pausedRef.current = false;

        await runScanQueue(newQueue, 0);
    };

    // Run the scan queue
    const runScanQueue = async (queueItems, startIndex) => {
        const newResults = [...results];

        for (let i = startIndex; i < queueItems.length; i++) {
            // Check for pause/cancel
            if (!scanningRef.current) {
                setQueueStatus('idle');
                return;
            }
            if (pausedRef.current) {
                setQueueStatus('paused');
                setCurrentIndex(i);
                return;
            }

            const item = queueItems[i];
            setCurrentIndex(i);

            // Update queue item status
            const updatedQueue = [...queueItems];
            updatedQueue[i] = { ...updatedQueue[i], status: 'scanning' };
            setQueue(updatedQueue);

            try {
                let analysis;

                // Different analysis for different sources
                if (scanSource === 'wordpress') {
                    // Use existing WordPress analysis
                    analysis = await analyzeArticleFull({
                        ...item,
                        content: item.content
                    }, `<!DOCTYPE html><html><head><title>${item.title}</title></head><body>${item.content}</body></html>`);
                } else {
                    // Fetch and analyze external URL
                    try {
                        analysis = await contentOptimizerService.analyzeURL(item.url);
                    } catch (fetchErr) {
                        throw new Error(`Failed to fetch: ${fetchErr.message}`);
                    }
                }

                // Calculate trend
                const previous = scanHistory.getLatest(item.slug || item.url);
                const trend = previous ? (analysis.overallScore || analysis.score) - previous.score : 0;

                // Save history
                if (item.slug) {
                    scanHistory.addScan(item.slug, analysis.overallScore || analysis.score, {
                        pseo: analysis.pseo?.score,
                        aeo: analysis.aeo?.score,
                        geo: analysis.geo?.score
                    });
                }

                // Aggregate issues
                const critical = (analysis.traditional?.summary?.critical || 0);
                const warning = (analysis.traditional?.summary?.warning || 0) +
                    (analysis.pseo?.summary?.warning || 0) +
                    (analysis.aeo?.summary?.warning || 0) +
                    (analysis.geo?.summary?.warning || 0);

                // Collect top issues
                const allChecks = [
                    ...(analysis.traditional?.checks || []),
                    ...(analysis.pseo?.checks || []),
                    ...(analysis.aeo?.checks || []),
                    ...(analysis.geo?.checks || []),
                    ...(analysis.issues || []).map(i => ({ label: i, score: 0 }))
                ];

                const topIssues = allChecks
                    .filter(c => (c.score || 0) < 100)
                    .sort((a, b) => (a.score || 0) - (b.score || 0))
                    .slice(0, 5);

                const result = {
                    id: item.id,
                    title: item.title,
                    slug: item.slug,
                    url: item.url,
                    category: item.category,
                    score: analysis.overallScore || analysis.score || 0,
                    grade: analysis.overallGrade || analysis.grade || 'N/A',
                    trend,
                    pseo: analysis.pseo?.score || 0,
                    aeo: analysis.aeo?.score || 0,
                    geo: analysis.geo?.score || 0,
                    critical,
                    warning,
                    wordCount: analysis.wordCount || 0,
                    questions: analysis.questions || 0,
                    authorityLinks: analysis.authorityLinks || 0,
                    hasTLDR: analysis.hasTLDR || false,
                    hasLastUpdated: analysis.hasLastUpdated || false,
                    topIssues,
                    aiVisibility: analysis.aiVisibility
                };

                newResults.push(result);
                updatedQueue[i] = { ...updatedQueue[i], status: 'done', score: result.score };
                setQueue([...updatedQueue]);
                setResults([...newResults]);

            } catch (err) {
                console.error(`Failed to analyze ${item.title}:`, err);
                updatedQueue[i] = { ...updatedQueue[i], status: 'error', error: err.message };
                setQueue([...updatedQueue]);
                newResults.push({
                    id: item.id,
                    title: item.title,
                    slug: item.slug,
                    url: item.url,
                    score: 0,
                    grade: 'F',
                    trend: 0,
                    error: err.message,
                    critical: 0, warning: 0, topIssues: []
                });
                setResults([...newResults]);
            }

            // Small delay to prevent overwhelming
            await new Promise(r => setTimeout(r, 100));
        }

        // Only mark as completed if we actually finished all items
        if (scanningRef.current && !pausedRef.current) {
            setQueueStatus('completed');
        }
        scanningRef.current = false;
    };

    // Pause scanning
    const handlePause = () => {
        pausedRef.current = true;
    };

    // Resume scanning
    const handleResume = () => {
        pausedRef.current = false;
        setQueueStatus('running');
        runScanQueue(queue, currentIndex);
    };

    // Cancel scanning
    const handleCancel = () => {
        scanningRef.current = false;
        pausedRef.current = false;
        setQueueStatus('idle');
    };

    // Clear queue
    const handleClearQueue = () => {
        setQueue([]);
        setQueueStatus('idle');
        setCurrentIndex(0);
    };

    // Load competitor data
    const handleLoadCompetitor = async (url) => {
        const urls = await feedParser.parseUrl(url, 'auto');
        const competitorQueue = urls.slice(0, 10).map((u, i) => ({
            id: `comp-${i}`,
            url: u,
            title: new URL(u).pathname.split('/').filter(Boolean).pop() || 'Untitled'
        }));

        // Quick scan competitor URLs
        const compResults = [];
        for (const item of competitorQueue) {
            try {
                const analysis = await contentOptimizerService.analyzeURL(item.url);
                compResults.push({
                    ...item,
                    score: analysis.score || 0,
                    wordCount: analysis.wordCount || 0,
                    questions: analysis.questions || 0,
                    authorityLinks: analysis.authorityLinks || 0
                });
            } catch (e) {
                compResults.push({ ...item, score: 0, error: e.message });
            }
        }
        setCompetitorResults(compResults);
    };

    // Export results to PDF
    const handleExportPDF = () => {
        if (results.length === 0) return;
        pdfExportService.exportBulkScan(results, `bulk-scan-${Date.now()}.pdf`);
    };

    // Toggle row expansion
    const toggleRow = (id) => {
        const newExpanded = new Set(expandedRows);
        if (newExpanded.has(id)) {
            newExpanded.delete(id);
        } else {
            newExpanded.add(id);
        }
        setExpandedRows(newExpanded);
    };

    // Filter and sort results
    const filteredResults = results.filter(r => {
        if (filters.search) {
            const search = filters.search.toLowerCase();
            if (!(r.title || '').toLowerCase().includes(search) &&
                !(r.url || '').toLowerCase().includes(search)) return false;
        }
        if (filters.category && r.category !== filters.category) return false;
        if (r.score < filters.scoreMin || r.score > filters.scoreMax) return false;
        return true;
    });

    const sortedResults = [...filteredResults].sort((a, b) => {
        const aVal = a[sortBy] || 0;
        const bVal = b[sortBy] || 0;
        return sortDir === 'asc' ? aVal - bVal : bVal - aVal;
    });

    const avgScore = results.length > 0
        ? Math.round(results.reduce((sum, r) => sum + (r.score || 0), 0) / results.length)
        : 0;

    // Source counts
    const sourceCounts = {
        wordpress: articles.length,
        manual: manualUrls.length,
        feed: feedUrls.length
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-gray-900 via-slate-900 to-gray-900 p-6">
            <div className="max-w-7xl mx-auto space-y-6">
                {/* Header */}
                <div className="flex items-center justify-between">
                    <div>
                        <h1 className="text-3xl font-bold text-white mb-2">Advanced Bulk Scan</h1>
                        <p className="text-gray-400">Analyze multiple articles with AI-powered insights</p>
                    </div>
                    <div className="flex gap-3">
                        {results.length > 0 && (
                            <button
                                onClick={handleExportPDF}
                                className="px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-xl flex items-center gap-2 transition-colors"
                            >
                                <Download className="w-5 h-5" />
                                Export PDF
                            </button>
                        )}
                        <button
                            onClick={handleStartScan}
                            disabled={queueStatus === 'running' || selectedArticles.size === 0}
                            className="px-6 py-3 bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white font-semibold rounded-xl disabled:opacity-50 flex items-center gap-2 shadow-lg shadow-purple-500/30"
                        >
                            {queueStatus === 'running' ? (
                                <Loader2 className="w-5 h-5 animate-spin" />
                            ) : (
                                <Play className="w-5 h-5" />
                            )}
                            {queueStatus === 'running' ? 'Scanning...' : `Scan ${selectedArticles.size} Articles`}
                        </button>
                    </div>
                </div>

                {/* Source Selector */}
                <ScanSourceSelector
                    source={scanSource}
                    onChange={setScanSource}
                    counts={sourceCounts}
                />

                {/* Source-specific Input */}
                {scanSource === 'manual' && (
                    <ManualUrlInput
                        onUrlsLoad={handleManualUrls}
                        initialUrls={manualUrls.map(u => u.url)}
                    />
                )}

                {scanSource === 'feed' && (
                    <FeedUrlInput onUrlsLoad={handleFeedUrls} />
                )}

                {/* Advanced Filters */}
                <AdvancedFilters
                    filters={filters}
                    onFiltersChange={setFilters}
                    categories={getCategories()}
                />

                {/* Article Selector - Show for all sources */}
                {!loading && getCurrentArticles().length > 0 && (
                    <ArticleSelector
                        articles={getFilteredArticles()}
                        selected={selectedArticles}
                        onSelectionChange={setSelectedArticles}
                        disabled={queueStatus === 'running'}
                    />
                )}

                {/* Scan Queue */}
                {queue.length > 0 && (
                    <ScanQueue
                        queue={queue}
                        currentIndex={currentIndex}
                        status={queueStatus}
                        onStart={handleStartScan}
                        onPause={handlePause}
                        onResume={handleResume}
                        onCancel={handleCancel}
                        onClear={handleClearQueue}
                    />
                )}

                {/* Results Summary */}
                {results.length > 0 && (
                    <div className="grid grid-cols-4 gap-4">
                        <SEOScoreCard title="Average Score" score={avgScore} icon={Layers} />
                        <div className="bg-green-500/10 border border-green-500/30 rounded-xl p-4 text-center">
                            <p className="text-3xl font-bold text-green-400">
                                {results.filter(r => r.score >= 70).length}
                            </p>
                            <p className="text-sm text-gray-400">Good (70+)</p>
                        </div>
                        <div className="bg-yellow-500/10 border border-yellow-500/30 rounded-xl p-4 text-center">
                            <p className="text-3xl font-bold text-yellow-400">
                                {results.filter(r => r.score >= 40 && r.score < 70).length}
                            </p>
                            <p className="text-sm text-gray-400">Needs Work</p>
                        </div>
                        <div className="bg-red-500/10 border border-red-500/30 rounded-xl p-4 text-center">
                            <p className="text-3xl font-bold text-red-400">
                                {results.filter(r => r.score < 40).length}
                            </p>
                            <p className="text-sm text-gray-400">Critical</p>
                        </div>
                    </div>
                )}

                {/* AI Insights & Competitor Comparison */}
                {results.length > 0 && (
                    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                        <AIInsights results={results} />
                        <CompetitorComparison
                            yourResults={results}
                            competitorResults={competitorResults}
                            onLoadCompetitor={handleLoadCompetitor}
                        />
                    </div>
                )}

                {/* Results Table */}
                {sortedResults.length > 0 && (
                    <div className="bg-slate-800/50 backdrop-blur-xl rounded-2xl border border-slate-700 overflow-hidden">
                        <div className="flex items-center justify-between px-6 py-4 bg-slate-900/50 border-b border-slate-700">
                            <h3 className="text-lg font-bold text-white flex items-center gap-2">
                                <FileText className="w-5 h-5 text-purple-400" />
                                Scan Results ({sortedResults.length})
                            </h3>
                        </div>
                        <table className="w-full">
                            <thead className="bg-slate-900/30">
                                <tr>
                                    <th className="w-10"></th>
                                    <th className="text-left px-6 py-3 text-xs font-medium text-gray-400">Title</th>
                                    <th
                                        className="text-center px-4 py-3 text-xs font-medium text-gray-400 cursor-pointer hover:text-white"
                                        onClick={() => { setSortBy('score'); setSortDir(sortDir === 'asc' ? 'desc' : 'asc'); }}
                                    >
                                        Score <ArrowUpDown className="w-3 h-3 inline" />
                                    </th>
                                    <th className="text-center px-4 py-3 text-xs font-medium text-gray-400">PSEO</th>
                                    <th className="text-center px-4 py-3 text-xs font-medium text-gray-400">AEO</th>
                                    <th className="text-center px-4 py-3 text-xs font-medium text-gray-400">GEO</th>
                                    <th className="text-center px-4 py-3 text-xs font-medium text-gray-400">Issues</th>
                                    <th className="text-right px-6 py-3 text-xs font-medium text-gray-400">Actions</th>
                                </tr>
                            </thead>
                            <tbody className="divide-y divide-slate-700">
                                {sortedResults.map((result) => (
                                    <React.Fragment key={result.id || result.url}>
                                        <tr
                                            className={`hover:bg-slate-700/30 cursor-pointer ${expandedRows.has(result.id) ? 'bg-slate-700/30' : ''}`}
                                            onClick={() => toggleRow(result.id)}
                                        >
                                            <td className="pl-4">
                                                {expandedRows.has(result.id)
                                                    ? <ChevronDown className="w-4 h-4 text-gray-400" />
                                                    : <ChevronRight className="w-4 h-4 text-gray-400" />
                                                }
                                            </td>
                                            <td className="px-6 py-4">
                                                <span className="text-white font-medium line-clamp-1">{result.title}</span>
                                                {result.url && (
                                                    <div className="text-xs text-gray-500 truncate max-w-xs">{result.url}</div>
                                                )}
                                            </td>
                                            <td className="px-4 py-4 text-center">
                                                <div className="flex flex-col items-center">
                                                    <span className={`font-bold ${result.score >= 70 ? 'text-green-400' : result.score >= 40 ? 'text-yellow-400' : 'text-red-400'}`}>
                                                        {result.score}
                                                    </span>
                                                    {result.trend !== 0 && (
                                                        <div className={`flex items-center text-xs mt-1 ${result.trend > 0 ? 'text-green-400' : 'text-red-400'}`}>
                                                            {result.trend > 0 ? <TrendingUp className="w-3 h-3 mr-0.5" /> : <TrendingDown className="w-3 h-3 mr-0.5" />}
                                                            {Math.abs(result.trend)}
                                                        </div>
                                                    )}
                                                </div>
                                            </td>
                                            <td className="px-4 py-4 text-center text-purple-400">{result.pseo || '-'}</td>
                                            <td className="px-4 py-4 text-center text-green-400">{result.aeo || '-'}</td>
                                            <td className="px-4 py-4 text-center text-blue-400">{result.geo || '-'}</td>
                                            <td className="px-4 py-4 text-center">
                                                <div className="flex items-center justify-center gap-2">
                                                    {result.critical > 0 && (
                                                        <span className="px-2 py-0.5 rounded bg-red-500/20 text-red-400 text-xs font-bold border border-red-500/30">
                                                            {result.critical}
                                                        </span>
                                                    )}
                                                    {result.warning > 0 && (
                                                        <span className="px-2 py-0.5 rounded bg-yellow-500/20 text-yellow-400 text-xs font-bold border border-yellow-500/30">
                                                            {result.warning}
                                                        </span>
                                                    )}
                                                    {result.critical === 0 && result.warning === 0 && (
                                                        <CheckCircle className="w-5 h-5 text-green-400" />
                                                    )}
                                                </div>
                                            </td>
                                            <td className="px-6 py-4 text-right">
                                                <a
                                                    href={result.url || `/articles/${result.slug}`}
                                                    target="_blank"
                                                    rel="noopener noreferrer"
                                                    onClick={(e) => e.stopPropagation()}
                                                    className="text-purple-400 hover:text-purple-300 text-sm font-medium mr-3"
                                                >
                                                    Open
                                                </a>
                                                <a
                                                    href={`/admin/content-optimizer?url=${encodeURIComponent(result.url || `${window.location.origin}/articles/${result.slug}`)}`}
                                                    onClick={(e) => e.stopPropagation()}
                                                    className="text-blue-400 hover:text-blue-300 text-sm font-medium"
                                                >
                                                    Details
                                                </a>
                                            </td>
                                        </tr>
                                        <AnimatePresence>
                                            {expandedRows.has(result.id) && (
                                                <motion.tr
                                                    initial={{ opacity: 0 }}
                                                    animate={{ opacity: 1 }}
                                                    exit={{ opacity: 0 }}
                                                >
                                                    <td colSpan="8" className="bg-slate-800/30 px-6 py-4 border-b border-slate-700">
                                                        <div className="ml-8">
                                                            <h4 className="text-sm font-bold text-gray-300 mb-3 flex items-center gap-2">
                                                                <AlertTriangle className="w-4 h-4 text-yellow-500" />
                                                                Top Priorities
                                                            </h4>
                                                            {result.topIssues && result.topIssues.length > 0 ? (
                                                                <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                                                                    {result.topIssues.map((issue, idx) => (
                                                                        <div key={idx} className="flex items-start gap-3 bg-slate-900/50 p-3 rounded-lg border border-slate-700/50">
                                                                            <div className="mt-0.5">
                                                                                {issue.score === 0
                                                                                    ? <AlertCircle className="w-4 h-4 text-red-500" />
                                                                                    : <AlertTriangle className="w-4 h-4 text-yellow-500" />
                                                                                }
                                                                            </div>
                                                                            <div>
                                                                                <p className="text-sm text-gray-200 font-medium">{issue.label}</p>
                                                                                {issue.message && (
                                                                                    <p className="text-xs text-gray-500 mt-1">{issue.message}</p>
                                                                                )}
                                                                            </div>
                                                                        </div>
                                                                    ))}
                                                                </div>
                                                            ) : (
                                                                <p className="text-sm text-gray-500 italic">No critical issues found! üéâ</p>
                                                            )}
                                                        </div>
                                                    </td>
                                                </motion.tr>
                                            )}
                                        </AnimatePresence>
                                    </React.Fragment>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}

                {/* Empty State */}
                {!loading && getCurrentArticles().length === 0 && (
                    <div className="bg-slate-800/50 rounded-2xl border border-slate-700 p-12 text-center">
                        <Layers className="w-12 h-12 text-gray-500 mx-auto mb-4" />
                        <p className="text-gray-400 mb-2">
                            {scanSource === 'wordpress'
                                ? 'No articles found from WordPress'
                                : scanSource === 'manual'
                                    ? 'Paste URLs to scan'
                                    : 'Enter a feed URL to load articles'
                            }
                        </p>
                        {scanSource === 'wordpress' && (
                            <button
                                onClick={loadArticles}
                                className="text-purple-400 hover:text-purple-300 text-sm font-medium"
                            >
                                Retry Loading
                            </button>
                        )}
                    </div>
                )}

                {loading && (
                    <div className="flex items-center justify-center py-12">
                        <Loader2 className="w-8 h-8 text-purple-500 animate-spin" />
                    </div>
                )}
            </div>
        </div>
    );
}

export default BulkScanPage;

/**
 * Interactive SEO Checklist Page
 * Manual checks for comprehensive SEO that automated tools might miss
 */

import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { CheckSquare, Square, Save, RotateCcw, ClipboardCheck, AlertCircle } from 'lucide-react';

const CHECKLIST_STORAGE_KEY = 'seo_toolkit_checklist_v1';

const CHECKLIST_SECTIONS = [
    {
        title: 'Content Quality',
        items: [
            { id: 'content_1', label: 'Keyword in H1 tag?' },
            { id: 'content_2', label: 'Keyword in first 100 words?' },
            { id: 'content_3', label: 'Content length > 1000 words?' },
            { id: 'content_4', label: 'Used subheadings (H2, H3) logically?' },
            { id: 'content_5', label: 'Images have descriptive Alt text?' }
        ]
    },
    {
        title: 'Technical SEO',
        items: [
            { id: 'tech_1', label: 'Fast page load speed (< 2s)?' },
            { id: 'tech_2', label: 'Mobile responsive design verified?' },
            { id: 'tech_3', label: 'Canonical tag is correct?' },
            { id: 'tech_4', label: 'No broken links?' },
            { id: 'tech_5', label: 'Schema markup verified?' }
        ]
    },
    {
        title: 'Meta Data',
        items: [
            { id: 'meta_1', label: 'Meta Title is unique & clickable?' },
            { id: 'meta_2', label: 'Meta Description includes CTA?' },
            { id: 'meta_3', label: 'URL slug is clean and short?' }
        ]
    }
];

export function ChecklistPage() {
    const [checkedItems, setCheckedItems] = useState({});
    const [progress, setProgress] = useState(0);

    useEffect(() => {
        const saved = localStorage.getItem(CHECKLIST_STORAGE_KEY);
        if (saved) {
            try {
                const parsed = JSON.parse(saved);
                setCheckedItems(parsed);
                updateProgress(parsed);
            } catch (e) {
                console.error('Failed to load checklist', e);
            }
        }
    }, []);

    const updateProgress = (items) => {
        const total = CHECKLIST_SECTIONS.reduce((acc, sec) => acc + sec.items.length, 0);
        const completed = Object.values(items).filter(Boolean).length;
        setProgress(Math.round((completed / total) * 100));
    };

    const toggleItem = (id) => {
        const newItems = { ...checkedItems, [id]: !checkedItems[id] };
        setCheckedItems(newItems);
        updateProgress(newItems);
        localStorage.setItem(CHECKLIST_STORAGE_KEY, JSON.stringify(newItems));
    };

    const resetChecklist = () => {
        if (window.confirm('Reset all progress?')) {
            setCheckedItems({});
            setProgress(0);
            localStorage.removeItem(CHECKLIST_STORAGE_KEY);
        }
    };

    return (
        <div className="space-y-6">
            <div className="flex items-center justify-between">
                <div>
                    <h1 className="text-3xl font-bold text-white mb-2">SEO Pre-Publish Checklist</h1>
                    <p className="text-gray-400">Manual verification for perfect optimization</p>
                </div>
                <div className="flex items-center gap-3">
                    <div className="flex flex-col items-end mr-4">
                        <span className="text-2xl font-bold text-green-400">{progress}%</span>
                        <span className="text-xs text-gray-400">Completed</span>
                    </div>
                    <button
                        onClick={resetChecklist}
                        className="p-2 text-gray-400 hover:text-white hover:bg-slate-700 rounded-lg transition-colors"
                        title="Reset Checklist"
                    >
                        <RotateCcw className="w-5 h-5" />
                    </button>
                </div>
            </div>

            {/* Progress Bar */}
            <div className="h-2 bg-slate-700/50 rounded-full overflow-hidden">
                <motion.div
                    initial={{ width: 0 }}
                    animate={{ width: `${progress}%` }}
                    className="h-full bg-gradient-to-r from-green-500 to-emerald-500"
                />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {CHECKLIST_SECTIONS.map((section, idx) => (
                    <motion.div
                        key={section.title}
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: idx * 0.1 }}
                        className="bg-slate-800/50 backdrop-blur-xl rounded-2xl border border-slate-700 p-6"
                    >
                        <h3 className="text-xl font-bold text-white mb-4 flex items-center gap-2">
                            <ClipboardCheck className="w-5 h-5 text-purple-400" />
                            {section.title}
                        </h3>
                        <div className="space-y-3">
                            {section.items.map((item) => (
                                <div
                                    key={item.id}
                                    onClick={() => toggleItem(item.id)}
                                    className={`flex items-start gap-3 p-3 rounded-xl cursor-pointer transition-all duration-200 border ${checkedItems[item.id]
                                            ? 'bg-green-500/10 border-green-500/30'
                                            : 'bg-slate-700/30 border-transparent hover:bg-slate-700/50'
                                        }`}
                                >
                                    <div className={`mt-0.5 ${checkedItems[item.id] ? 'text-green-400' : 'text-gray-500'}`}>
                                        {checkedItems[item.id] ? <CheckSquare className="w-5 h-5" /> : <Square className="w-5 h-5" />}
                                    </div>
                                    <span className={`${checkedItems[item.id] ? 'text-gray-200 line-through decoration-gray-500/50' : 'text-gray-300'}`}>
                                        {item.label}
                                    </span>
                                </div>
                            ))}
                        </div>
                    </motion.div>
                ))}
            </div>

            <div className="bg-blue-500/10 border border-blue-500/20 rounded-xl p-4 flex items-start gap-3">
                <AlertCircle className="w-5 h-5 text-blue-400 mt-0.5" />
                <div>
                    <h4 className="text-sm font-bold text-blue-400">Pro Tip</h4>
                    <p className="text-sm text-gray-400 mt-1">
                        Use this checklist alongside the <strong>SEO Scanner</strong>. While the scanner finds technical issues, this checklist ensures human-centric quality checks like content flow and readability.
                    </p>
                </div>
            </div>
        </div>
    );
}

export default ChecklistPage;

// src/pages/admin/ComparePage.jsx
/**
 * Compare Page - Side-by-side URL comparison with AI citation metrics
 */

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { GitCompare, Loader2, Globe, Trophy, ArrowRight, TrendingUp, TrendingDown, Minus, Link as LinkIcon, Download } from 'lucide-react';
import contentOptimizerService from '../../services/contentOptimizerService';
import pdfExportService from '../../services/pdfExportService';

export function ComparePage() {
    const [url1, setUrl1] = useState('');
    const [url2, setUrl2] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [results, setResults] = useState(null);

    const handleCompare = async () => {
        if (!url1.trim() || !url2.trim()) {
            setError('Please enter both URLs');
            return;
        }

        setLoading(true);
        setError('');
        setResults(null);

        try {
            const [analysis1, analysis2] = await Promise.all([
                contentOptimizerService.analyzeURL(url1),
                contentOptimizerService.analyzeURL(url2)
            ]);

            if (!analysis1.success) {
                throw new Error(`Failed to analyze URL 1: ${analysis1.error}`);
            }
            if (!analysis2.success) {
                throw new Error(`Failed to analyze URL 2: ${analysis2.error}`);
            }

            setResults({
                report1: analysis1.data,
                report2: analysis2.data
            });
        } catch (err) {
            setError(`Comparison failed: ${err.message}`);
        } finally {
            setLoading(false);
        }
    };

    const getWinner = (val1, val2) => {
        if (val1 > val2) return 'you';
        if (val2 > val1) return 'competitor';
        return 'tie';
    };

    const getDiffIndicator = (val1, val2) => {
        const diff = val1 - val2;
        if (diff > 0) return { icon: TrendingUp, color: 'text-green-400', text: `+${diff}` };
        if (diff < 0) return { icon: TrendingDown, color: 'text-red-400', text: `${diff}` };
        return { icon: Minus, color: 'text-gray-400', text: '0' };
    };

    const handleExportPDF = () => {
        if (!results) return;
        const filename = `seo-comparison-${Date.now()}.pdf`;
        pdfExportService.exportComparison(results.report1, results.report2, filename);
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-gray-900 via-slate-900 to-gray-900 p-6">
            <div className="max-w-7xl mx-auto">
                {/* Header */}
                <div className="mb-8 text-center">
                    <div className="inline-flex items-center gap-2 mb-4">
                        <div className="p-3 bg-gradient-to-br from-purple-500 to-pink-600 rounded-xl shadow-lg shadow-purple-500/50">
                            <GitCompare className="w-8 h-8 text-white" />
                        </div>
                    </div>
                    <h1 className="text-4xl font-bold bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text text-transparent mb-2">
                        Competitor Analysis
                    </h1>
                    <p className="text-gray-400 text-lg">Compare your content against competitors</p>
                </div>

                {/* URL Input Cards */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
                    {/* Your URL */}
                    <div className="bg-gray-800/50 backdrop-blur-xl rounded-2xl shadow-2xl border border-gray-700/50 p-6">
                        <div className="flex items-center gap-2 mb-4">
                            <Globe className="w-5 h-5 text-blue-400" />
                            <h3 className="text-lg font-bold text-white">Your URL</h3>
                        </div>
                        <input
                            type="url"
                            value={url1}
                            onChange={(e) => setUrl1(e.target.value)}
                            placeholder="https://your-site.com/article"
                            className="w-full px-4 py-3 bg-gray-900/50 border-2 border-gray-600 rounded-xl focus:ring-4 focus:ring-blue-500/50 focus:border-blue-500 transition-all text-white placeholder-gray-500"
                            disabled={loading}
                        />
                    </div>

                    {/* Competitor URL */}
                    <div className="bg-gray-800/50 backdrop-blur-xl rounded-2xl shadow-2xl border border-gray-700/50 p-6">
                        <div className="flex items-center gap-2 mb-4">
                            <Trophy className="w-5 h-5 text-purple-400" />
                            <h3 className="text-lg font-bold text-white">Competitor URL</h3>
                        </div>
                        <input
                            type="url"
                            value={url2}
                            onChange={(e) => setUrl2(e.target.value)}
                            placeholder="https://competitor.com/article"
                            className="w-full px-4 py-3 bg-gray-900/50 border-2 border-gray-600 rounded-xl focus:ring-4 focus:ring-purple-500/50 focus:border-purple-500 transition-all text-white placeholder-gray-500"
                            disabled={loading}
                        />
                    </div>
                </div>

                {/* Compare Button */}
                <div className="flex justify-center mb-8">
                    <button
                        onClick={handleCompare}
                        disabled={loading}
                        className="px-8 py-4 bg-gradient-to-r from-purple-600 to-pink-600 text-white rounded-xl hover:from-purple-700 hover:to-pink-700 disabled:from-gray-600 disabled:to-gray-700 disabled:cursor-not-allowed flex items-center gap-3 transition-all shadow-lg shadow-purple-500/50 hover:shadow-xl hover:shadow-purple-500/60 transform hover:scale-105 disabled:transform-none font-semibold text-lg"
                    >
                        {loading ? (
                            <>
                                <Loader2 className="w-6 h-6 animate-spin" />
                                Analyzing...
                            </>
                        ) : (
                            <>
                                <GitCompare className="w-6 h-6" />
                                Compare
                            </>
                        )}
                    </button>
                </div>

                {/* Error Message */}
                {error && (
                    <div className="bg-gradient-to-r from-red-900/30 to-pink-900/30 border-2 border-red-500/50 rounded-2xl p-6 mb-8 backdrop-blur-xl">
                        <p className="text-red-300 text-center">{error}</p>
                    </div>
                )}

                {/* Results */}
                {results && (
                    <motion.div
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        className="space-y-6"
                    >
                        {/* Winner Banner */}
                        {results.report1.score !== results.report2.score && (
                            <div className={`rounded-2xl p-6 text-center backdrop-blur-xl ${results.report1.score > results.report2.score
                                ? 'bg-gradient-to-r from-green-900/30 to-emerald-900/30 border-2 border-green-500/50'
                                : 'bg-gradient-to-r from-purple-900/30 to-pink-900/30 border-2 border-purple-500/50'
                                }`}>
                                <Trophy className="w-12 h-12 mx-auto mb-3 text-yellow-400" />
                                <h2 className="text-2xl font-bold text-white mb-2">
                                    {results.report1.score > results.report2.score ? 'Your Page Wins! üéâ' : 'Competitor Wins'}
                                </h2>
                                <p className="text-gray-300">
                                    Score: {Math.max(results.report1.score, results.report2.score)}/100
                                </p>
                            </div>
                        )}

                        {/* Comparison Table */}
                        <div className="bg-gray-800/50 backdrop-blur-xl rounded-2xl shadow-2xl border border-gray-700/50 overflow-hidden">
                            <div className="p-6 border-b border-gray-700/50">
                                <h3 className="text-2xl font-bold text-white">Detailed Comparison</h3>
                            </div>
                            <div className="overflow-x-auto">
                                <table className="w-full">
                                    <thead className="bg-gray-900/50">
                                        <tr>
                                            <th className="px-6 py-4 text-left text-sm font-semibold text-gray-300">Metric</th>
                                            <th className="px-6 py-4 text-center text-sm font-semibold text-blue-400">You</th>
                                            <th className="px-6 py-4 text-center text-sm font-semibold text-gray-400">Diff</th>
                                            <th className="px-6 py-4 text-center text-sm font-semibold text-purple-400">Competitor</th>
                                        </tr>
                                    </thead>
                                    <tbody className="divide-y divide-gray-700/50">
                                        {[
                                            { label: 'Overall Score', key: 'score', suffix: '/100' },
                                            { label: 'Word Count', key: 'wordCount', suffix: '' },
                                            { label: 'Statistics', key: 'statistics', suffix: '' },
                                            { label: 'Data Tables', key: 'tables', suffix: '' },
                                            { label: 'Questions', key: 'questions', suffix: '' },
                                            { label: 'Internal Links', key: 'internalLinks', suffix: '' },
                                            { label: 'External Links', key: 'externalLinks', suffix: '' },
                                            { label: 'Authority Links', key: 'authorityLinks', suffix: '' },
                                            { label: 'Code Examples', key: 'codeBlocks', suffix: '' }
                                        ].map((metric, idx) => {
                                            const val1 = results.report1[metric.key] || 0;
                                            const val2 = results.report2[metric.key] || 0;
                                            const winner = getWinner(val1, val2);
                                            const diff = getDiffIndicator(val1, val2);
                                            const DiffIcon = diff.icon;

                                            return (
                                                <tr key={idx} className="hover:bg-gray-700/30 transition-colors">
                                                    <td className="px-6 py-4 text-sm font-medium text-gray-300">{metric.label}</td>
                                                    <td className={`px-6 py-4 text-center text-lg font-bold ${winner === 'you' ? 'text-green-400' : winner === 'tie' ? 'text-gray-400' : 'text-gray-500'
                                                        }`}>
                                                        {val1}{metric.suffix}
                                                        {winner === 'you' && ' ‚úì'}
                                                    </td>
                                                    <td className="px-6 py-4 text-center">
                                                        <div className={`inline-flex items-center gap-1 ${diff.color}`}>
                                                            <DiffIcon className="w-4 h-4" />
                                                            <span className="text-sm font-medium">{diff.text}</span>
                                                        </div>
                                                    </td>
                                                    <td className={`px-6 py-4 text-center text-lg font-bold ${winner === 'competitor' ? 'text-purple-400' : winner === 'tie' ? 'text-gray-400' : 'text-gray-500'
                                                        }`}>
                                                        {val2}{metric.suffix}
                                                        {winner === 'competitor' && ' ‚úì'}
                                                    </td>
                                                </tr>
                                            );
                                        })}
                                    </tbody>
                                </table>
                            </div>
                        </div>

                        {/* Feature Comparison */}
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            {/* Your Strengths */}
                            <div className="bg-gray-800/50 backdrop-blur-xl rounded-2xl shadow-2xl border border-gray-700/50 p-6">
                                <h3 className="text-xl font-bold text-white mb-4">Your Strengths</h3>
                                <div className="space-y-2">
                                    {results.report1.strengths.length > 0 ? (
                                        results.report1.strengths.map((strength, idx) => (
                                            <div key={idx} className="flex items-center gap-2 text-green-300 bg-green-900/20 px-3 py-2 rounded-lg">
                                                <span className="text-sm">‚úì {strength}</span>
                                            </div>
                                        ))
                                    ) : (
                                        <p className="text-gray-500 text-sm">No strengths detected</p>
                                    )}
                                </div>
                            </div>

                            {/* Competitor Strengths */}
                            <div className="bg-gray-800/50 backdrop-blur-xl rounded-2xl shadow-2xl border border-gray-700/50 p-6">
                                <h3 className="text-xl font-bold text-white mb-4">Competitor Strengths</h3>
                                <div className="space-y-2">
                                    {results.report2.strengths.length > 0 ? (
                                        results.report2.strengths.map((strength, idx) => (
                                            <div key={idx} className="flex items-center gap-2 text-purple-300 bg-purple-900/20 px-3 py-2 rounded-lg">
                                                <span className="text-sm">‚úì {strength}</span>
                                            </div>
                                        ))
                                    ) : (
                                        <p className="text-gray-500 text-sm">No strengths detected</p>
                                    )}
                                </div>
                            </div>
                        </div>

                        {/* Recommendations */}
                        {results.report1.recommendations.length > 0 && (
                            <div className="bg-gray-800/50 backdrop-blur-xl rounded-2xl shadow-2xl border border-gray-700/50 p-6">
                                <h3 className="text-2xl font-bold text-white mb-4">Your Improvement Opportunities</h3>
                                <div className="space-y-3">
                                    {results.report1.recommendations.slice(0, 5).map((rec, idx) => (
                                        <div key={idx} className="bg-gray-900/50 rounded-lg p-4 border border-gray-700/50">
                                            <div className="flex items-center gap-2 mb-2">
                                                <span className={`px-2 py-1 rounded text-xs font-bold ${rec.priority === 'HIGH' ? 'bg-red-500/20 text-red-300' :
                                                    rec.priority === 'MEDIUM' ? 'bg-yellow-500/20 text-yellow-300' :
                                                        'bg-blue-500/20 text-blue-300'
                                                    }`}>
                                                    {rec.priority}
                                                </span>
                                                <span className="font-semibold text-white">{rec.type}</span>
                                            </div>
                                            <p className="text-sm text-gray-300 mb-1">
                                                <strong>Action:</strong> {rec.action}
                                            </p>
                                            <p className="text-sm text-blue-300">
                                                <strong>Impact:</strong> {rec.impact}
                                            </p>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}
                    </motion.div>
                )}

                {/* Empty State */}
                {!results && !error && !loading && (
                    <div className="bg-gray-800/50 backdrop-blur-xl rounded-2xl p-16 text-center border-2 border-dashed border-gray-600 shadow-2xl">
                        <GitCompare className="w-20 h-20 mx-auto mb-6 text-purple-400" />
                        <h3 className="text-3xl font-bold text-white mb-3">Ready to Compare</h3>
                        <p className="text-gray-400 text-lg mb-6">Enter both URLs above to see detailed comparison</p>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 max-w-md mx-auto text-left">
                            {[
                                'AI citation scores',
                                'Content depth analysis',
                                'Link profile comparison',
                                'Actionable recommendations'
                            ].map((feature, idx) => (
                                <div key={idx} className="flex items-center gap-2 text-gray-300 bg-gray-700/30 px-4 py-2 rounded-lg">
                                    <ArrowRight className="w-4 h-4 text-purple-400" />
                                    <span className="text-sm">{feature}</span>
                                </div>
                            ))}
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default ComparePage;

import React, { useState } from 'react';
import { Search, TrendingUp, AlertCircle, CheckCircle, Clock, FileText, Sparkles, Target, Zap, Link as LinkIcon, Download } from 'lucide-react';
import contentOptimizerService from '../../services/contentOptimizerService';
import pdfExportService from '../../services/pdfExportService';

const ContentOptimizerPage = () => {
    const [url, setUrl] = useState('');
    const [loading, setLoading] = useState(false);
    const [result, setResult] = useState(null);
    const [error, setError] = useState(null);

    const handleAnalyze = async () => {
        if (!url.trim()) {
            setError('Please enter a URL');
            return;
        }

        setLoading(true);
        setError(null);
        setResult(null);

        const analysis = await contentOptimizerService.analyzeURL(url);

        setLoading(false);

        if (analysis.success) {
            setResult(analysis.data);
        } else {
            setError(analysis.error);
        }
    };

    const getScoreGradient = (score) => {
        if (score >= 85) return 'from-green-400 to-emerald-500';
        if (score >= 70) return 'from-yellow-400 to-orange-400';
        return 'from-red-400 to-pink-500';
    };

    const getScoreBg = (score) => {
        if (score >= 85) return 'bg-gradient-to-br from-green-900/20 to-emerald-900/20';
        if (score >= 70) return 'bg-gradient-to-br from-yellow-900/20 to-orange-900/20';
        return 'bg-gradient-to-br from-red-900/20 to-pink-900/20';
    };

    const getScoreBorder = (score) => {
        if (score >= 85) return 'border-green-500/30';
        if (score >= 70) return 'border-yellow-500/30';
        return 'border-red-500/30';
    };

    const getPriorityBadge = (priority) => {
        const colors = {
            HIGH: 'bg-gradient-to-r from-red-500 to-pink-600 text-white shadow-lg shadow-red-500/50',
            MEDIUM: 'bg-gradient-to-r from-yellow-500 to-orange-500 text-white shadow-lg shadow-yellow-500/50',
            LOW: 'bg-gradient-to-r from-blue-500 to-indigo-600 text-white shadow-lg shadow-blue-500/50'
        };
        return colors[priority] || colors.MEDIUM;
    };

    const handleExportPDF = () => {
        if (!result) return;
        const filename = `seo-analysis-${new URL(result.url).hostname}-${Date.now()}.pdf`;
        pdfExportService.exportSingleAnalysis(result, filename);
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-gray-900 via-slate-900 to-gray-900 p-6">
            <div className="max-w-6xl mx-auto">
                {/* Header */}
                <div className="mb-8 text-center">
                    <div className="inline-flex items-center gap-2 mb-4">
                        <div className="p-3 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-xl shadow-lg shadow-blue-500/50">
                            <Sparkles className="w-8 h-8 text-white" />
                        </div>
                    </div>
                    <h1 className="text-4xl font-bold bg-gradient-to-r from-blue-400 to-indigo-400 bg-clip-text text-transparent mb-2">
                        Content Optimizer
                    </h1>
                    <p className="text-gray-400 text-lg">Analyze any URL for AI citation optimization</p>
                </div>

                {/* URL Input Card */}
                <div className="bg-gray-800/50 backdrop-blur-xl rounded-2xl shadow-2xl border border-gray-700/50 p-8 mb-8">
                    <div className="flex gap-4">
                        <div className="flex-1 relative">
                            <input
                                type="url"
                                value={url}
                                onChange={(e) => setUrl(e.target.value)}
                                onKeyPress={(e) => e.key === 'Enter' && handleAnalyze()}
                                placeholder="https://example.com/article"
                                className="w-full px-6 py-4 bg-gray-900/50 border-2 border-gray-600 rounded-xl focus:ring-4 focus:ring-blue-500/50 focus:border-blue-500 transition-all text-lg text-white placeholder-gray-500"
                                disabled={loading}
                            />
                        </div>
                        <button
                            onClick={handleAnalyze}
                            disabled={loading}
                            className="px-8 py-4 bg-gradient-to-r from-blue-600 to-indigo-600 text-white rounded-xl hover:from-blue-700 hover:to-indigo-700 disabled:from-gray-600 disabled:to-gray-700 disabled:cursor-not-allowed flex items-center gap-3 transition-all shadow-lg shadow-blue-500/50 hover:shadow-xl hover:shadow-blue-500/60 transform hover:scale-105 disabled:transform-none font-semibold text-lg"
                        >
                            {loading ? (
                                <>
                                    <Clock className="w-6 h-6 animate-spin" />
                                    Analyzing...
                                </>
                            ) : (
                                <>
                                    <Search className="w-6 h-6" />
                                    Analyze
                                </>
                            )}
                        </button>
                    </div>
                </div>

                {/* Error Message */}
                {error && (
                    <div className="bg-gradient-to-r from-red-900/30 to-pink-900/30 border-2 border-red-500/50 rounded-2xl p-6 mb-8 flex items-start gap-4 shadow-lg shadow-red-500/20 animate-shake backdrop-blur-xl">
                        <div className="p-2 bg-red-500/20 rounded-lg">
                            <AlertCircle className="w-6 h-6 text-red-400" />
                        </div>
                        <div>
                            <h3 className="font-bold text-red-300 text-lg mb-1">Error</h3>
                            <p className="text-red-200">{error}</p>
                        </div>
                    </div>
                )}

                {/* Results */}
                {result && (
                    <div className="space-y-6 animate-fadeIn">
                        {/* Score Card */}
                        <div className={`${getScoreBg(result.score)} backdrop-blur-xl rounded-2xl p-8 border-2 ${getScoreBorder(result.score)} shadow-2xl`}>
                            <div className="flex items-center justify-between mb-6">
                                <div>
                                    <h2 className="text-3xl font-bold text-white mb-2 flex items-center gap-2">
                                        <Target className="w-8 h-8 text-blue-400" />
                                        Optimization Score
                                    </h2>
                                    <p className="text-gray-400 text-sm truncate max-w-md">{result.url}</p>
                                </div>
                                <div className={`text-7xl font-black bg-gradient-to-r ${getScoreGradient(result.score)} bg-clip-text text-transparent`}>
                                    {result.score}
                                    <span className="text-4xl">/100</span>
                                </div>
                            </div>

                            {/* Progress Bar */}
                            <div className="w-full bg-gray-700/50 rounded-full h-4 mb-6 overflow-hidden shadow-inner">
                                <div
                                    className={`h-4 rounded-full transition-all duration-1000 ease-out bg-gradient-to-r ${getScoreGradient(result.score)} shadow-lg`}
                                    style={{ width: `${result.score}%` }}
                                />
                            </div>

                            {/* Quick Stats */}
                            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                                {[
                                    { label: 'Words', value: result.wordCount, icon: FileText },
                                    { label: 'Statistics', value: result.statistics, icon: TrendingUp },
                                    { label: 'Questions', value: result.questions, icon: Sparkles },
                                    { label: 'Authority Links', value: result.authorityLinks, icon: Zap },
                                    { label: 'Internal Links', value: result.internalLinks || 0, icon: LinkIcon },
                                    { label: 'External Links', value: result.externalLinks, icon: LinkIcon }
                                ].map((stat, idx) => (
                                    <div key={idx} className="bg-gray-800/60 backdrop-blur-sm rounded-xl p-4 text-center border border-gray-700/50 shadow-md">
                                        <stat.icon className="w-6 h-6 mx-auto mb-2 text-gray-400" />
                                        <div className="text-3xl font-bold text-white">{stat.value}</div>
                                        <div className="text-sm text-gray-400 font-medium">{stat.label}</div>
                                    </div>
                                ))}
                            </div>
                        </div>

                        {/* AI Visibility Score */}
                        {result.aiVisibility && (
                            <div className="bg-gradient-to-br from-purple-900/30 to-pink-900/30 backdrop-blur-xl rounded-2xl p-6 border-2 border-purple-500/30 shadow-2xl">
                                <div className="flex items-center justify-between mb-4">
                                    <div>
                                        <h3 className="text-2xl font-bold text-white mb-1 flex items-center gap-2">
                                            <Sparkles className="w-7 h-7 text-purple-400" />
                                            AI Visibility Score
                                        </h3>
                                        <p className="text-gray-400 text-sm">Probability of AI citation</p>
                                    </div>
                                    <div className="text-right">
                                        <div className="text-5xl font-black bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text text-transparent">
                                            {result.aiVisibility.score}%
                                        </div>
                                        <div className={`text-sm font-bold mt-1 ${result.aiVisibility.citationProbability === 'Very High' ? 'text-green-400' :
                                            result.aiVisibility.citationProbability === 'High' ? 'text-blue-400' :
                                                result.aiVisibility.citationProbability === 'Medium' ? 'text-yellow-400' :
                                                    'text-red-400'
                                            }`}>
                                            {result.aiVisibility.citationProbability} Citation Probability
                                        </div>
                                    </div>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                                    {result.aiVisibility.factors.map((factor, idx) => (
                                        <div key={idx} className={`flex items-center justify-between px-4 py-2 rounded-lg ${factor.status === 'good' ? 'bg-green-900/20 border border-green-500/30' :
                                            factor.status === 'moderate' ? 'bg-yellow-900/20 border border-yellow-500/30' :
                                                'bg-red-900/20 border border-red-500/30'
                                            }`}>
                                            <span className={`text-sm font-medium ${factor.status === 'good' ? 'text-green-300' :
                                                factor.status === 'moderate' ? 'text-yellow-300' :
                                                    'text-red-300'
                                                }`}>
                                                {factor.factor}
                                            </span>
                                            <span className={`text-xs font-bold ${factor.status === 'good' ? 'text-green-400' :
                                                factor.status === 'moderate' ? 'text-yellow-400' :
                                                    'text-red-400'
                                                }`}>
                                                {factor.impact}
                                            </span>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        {/* Competitor Suggestions */}
                        {result.competitorSuggestions && result.competitorSuggestions.length > 0 && (
                            <div className="bg-gray-800/50 backdrop-blur-xl rounded-2xl shadow-2xl border border-gray-700/50 p-6">
                                <h3 className="text-2xl font-bold text-white mb-4 flex items-center gap-3">
                                    <div className="p-2 bg-purple-500/20 rounded-lg">
                                        <Target className="w-6 h-6 text-purple-400" />
                                    </div>
                                    Suggested Competitors to Analyze
                                </h3>
                                <p className="text-gray-400 mb-4 text-sm">
                                    Based on your content keywords: <span className="text-purple-400 font-semibold">{result.keywords.slice(0, 5).join(', ')}</span>
                                </p>
                                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                                    {result.competitorSuggestions.map((suggestion, idx) => (
                                        <a
                                            key={idx}
                                            href={suggestion.searchUrl}
                                            target="_blank"
                                            rel="noopener noreferrer"
                                            className="group bg-gradient-to-br from-gray-900/50 to-gray-800/50 rounded-xl p-4 border border-gray-700/50 hover:border-purple-500/50 transition-all hover:shadow-lg hover:shadow-purple-500/20"
                                        >
                                            <div className="flex items-center gap-2 mb-2">
                                                <LinkIcon className="w-5 h-5 text-purple-400" />
                                                <span className="font-bold text-white group-hover:text-purple-400 transition-colors">
                                                    {suggestion.domain}
                                                </span>
                                            </div>
                                            <p className="text-xs text-gray-400 mb-3">{suggestion.reason}</p>
                                            <div className="flex items-center gap-1 text-xs text-purple-400 font-semibold">
                                                <Search className="w-4 h-4" />
                                                Find similar articles ‚Üí
                                            </div>
                                        </a>
                                    ))}
                                </div>
                            </div>
                        )}

                        {/* Strengths */}
                        {result.strengths.length > 0 && (
                            <div className="bg-gray-800/50 backdrop-blur-xl rounded-2xl shadow-2xl border border-gray-700/50 p-6">
                                <h3 className="text-2xl font-bold text-white mb-4 flex items-center gap-3">
                                    <div className="p-2 bg-green-500/20 rounded-lg">
                                        <CheckCircle className="w-6 h-6 text-green-400" />
                                    </div>
                                    Strengths ({result.strengths.length})
                                </h3>
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                                    {result.strengths.map((strength, index) => (
                                        <div key={index} className="flex items-center gap-3 text-green-300 bg-gradient-to-r from-green-900/30 to-emerald-900/30 px-4 py-3 rounded-xl border border-green-500/30 shadow-sm">
                                            <CheckCircle className="w-5 h-5 flex-shrink-0" />
                                            <span className="text-sm font-medium">{strength}</span>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        {/* Issues */}
                        {result.issues.length > 0 && (
                            <div className="bg-gray-800/50 backdrop-blur-xl rounded-2xl shadow-2xl border border-gray-700/50 p-6">
                                <h3 className="text-2xl font-bold text-white mb-4 flex items-center gap-3">
                                    <div className="p-2 bg-red-500/20 rounded-lg">
                                        <AlertCircle className="w-6 h-6 text-red-400" />
                                    </div>
                                    Issues Found ({result.issues.length})
                                </h3>
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                                    {result.issues.map((issue, index) => (
                                        <div key={index} className="flex items-center gap-3 text-red-300 bg-gradient-to-r from-red-900/30 to-pink-900/30 px-4 py-3 rounded-xl border border-red-500/30 shadow-sm">
                                            <AlertCircle className="w-5 h-5 flex-shrink-0" />
                                            <span className="text-sm font-medium">{issue}</span>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        {/* Recommendations */}
                        {result.recommendations.length > 0 && (
                            <div className="bg-gray-800/50 backdrop-blur-xl rounded-2xl shadow-2xl border border-gray-700/50 p-6">
                                <h3 className="text-2xl font-bold text-white mb-4 flex items-center gap-3">
                                    <div className="p-2 bg-blue-500/20 rounded-lg">
                                        <TrendingUp className="w-6 h-6 text-blue-400" />
                                    </div>
                                    Recommendations ({result.recommendations.length})
                                </h3>
                                <div className="space-y-4">
                                    {result.recommendations.map((rec, index) => (
                                        <div key={index} className="border-2 border-gray-700/50 rounded-xl p-5 hover:border-blue-500/50 hover:shadow-lg hover:shadow-blue-500/20 transition-all bg-gradient-to-br from-gray-800/50 to-gray-900/50 backdrop-blur-sm">
                                            <div className="flex items-start justify-between mb-3">
                                                <div className="flex items-center gap-3">
                                                    <span className={`px-3 py-1 rounded-lg text-xs font-bold ${getPriorityBadge(rec.priority)}`}>
                                                        {rec.priority}
                                                    </span>
                                                    <span className="font-bold text-white text-lg">{rec.type}</span>
                                                </div>
                                            </div>
                                            <div className="space-y-2">
                                                <p className="text-sm text-gray-300">
                                                    <strong className="text-white">Issue:</strong> {rec.issue}
                                                </p>
                                                <p className="text-sm text-gray-300">
                                                    <strong className="text-white">Action:</strong> {rec.action}
                                                </p>
                                                <p className="text-sm text-blue-300 bg-gradient-to-r from-blue-900/30 to-indigo-900/30 px-4 py-2 rounded-lg border border-blue-500/30">
                                                    <strong>Impact:</strong> {rec.impact}
                                                </p>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}
                    </div>
                )}

                {/* Empty State */}
                {!result && !error && !loading && (
                    <div className="bg-gray-800/50 backdrop-blur-xl rounded-2xl p-16 text-center border-2 border-dashed border-gray-600 shadow-2xl">
                        <div className="inline-flex p-6 bg-gradient-to-br from-blue-900/50 to-indigo-900/50 rounded-2xl mb-6">
                            <FileText className="w-20 h-20 text-blue-400" />
                        </div>
                        <h3 className="text-3xl font-bold text-white mb-3">Ready to Analyze</h3>
                        <p className="text-gray-400 mb-6 text-lg">Enter a URL above to get AI citation optimization insights</p>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 max-w-md mx-auto text-left">
                            {[
                                'Checks for TL;DR summaries',
                                'Analyzes statistics and data',
                                'Detects FAQ opportunities',
                                'Validates freshness signals',
                                'Counts internal & external links',
                                'Measures content depth'
                            ].map((feature, idx) => (
                                <div key={idx} className="flex items-center gap-2 text-gray-300 bg-gray-700/30 px-4 py-2 rounded-lg border border-gray-600/50">
                                    <CheckCircle className="w-5 h-5 text-green-400 flex-shrink-0" />
                                    <span className="text-sm font-medium">{feature}</span>
                                </div>
                            ))}
                        </div>
                    </div>
                )}
            </div>

            <style jsx>{`
                @keyframes fadeIn {
                    from { opacity: 0; transform: translateY(20px); }
                    to { opacity: 1; transform: translateY(0); }
                }
                @keyframes shake {
                    0%, 100% { transform: translateX(0); }
                    25% { transform: translateX(-10px); }
                    75% { transform: translateX(10px); }
                }
                .animate-fadeIn {
                    animation: fadeIn 0.5s ease-out;
                }
                .animate-shake {
                    animation: shake 0.5s ease-in-out;
                }
            `}</style>
        </div>
    );
};

