                            <TrendingUp className="w-6 h-6 text-green-400" />
                        </div>
                        <div>
                            <p className="text-2xl font-bold text-white">30+</p>
                            <p className="text-sm text-gray-400">SEO Checks</p>
                        </div>
                    </div>
                </motion.div>

